INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(12,0,1,2,'Grate 1 cups radishes into small pieces.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(14,2,0,2,'Watch little chef as they grate radishes.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(15,3,1,2,'Chop the chives or green onions.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(16,4,1,2,'In a medium bowl, mix together the butter and cream cheese. Mix in grated radishes, 2 tbsp chives or green onions and salt, combining thoroughly');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(28,0,1,3,'Chop 2 onions and 2 garlic cloves.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(29,1,0,3,'Warm the oil in a large pot on medium heat. Add the onion and garlic. Add the beef and allow it to brown on the outside.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(30,2,1,3,'Add 4 tbsp paprika and stir till meat is covered.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(31,3,1,3,'Add 3 tbsp Wondra flour and tomato paste just until the juice is soaked up.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(32,4,1,3,'Add the water make sure it covers the meat completely and add extra water so it can cook well.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(33,5,1,3,'Mix flour in well to remove all the lumps.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(35,7,0,3,'Add salt and pepper. Bring to a boil.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(37,0,0,4,'Place diced bacon in large pot on medium heat and cook until crisp.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(38,1,0,4,'Add onions to the bacon and cook until the onions are clear (for about 5 minutes).');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(39,2,1,4,'Add potatoes, water, salt and pepper to the pot. Bring to a boil and cook for about 15 minutes in medium to low temperature or until potatoes are very tender.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(40,3,1,4,'Then add the butter and the cream. Drain the clams and reserve the liquid left aside.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(41,4,1,4,'Then stir the clams into the soup and cook for another 5 minutes. Use the remaining water to dilute the cream if necessary (it’s up to your personal choice of creamy).');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(42,0,0,5,'Bring a large pot of water to a boil. Add potatoes, and cook for 15 to 20 minutes, or until tender but still firm.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(48,0,0,6,'Preheat the oven to 325 F degrees and spray a non-sticking pan with non-sticking spray.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(49,1,1,6,'Mix all ingredients together in a big bowl. Make sure that there are no bumps when mixing!');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(50,2,1,6,'Now that the mix is ready you can pour in the pan and put it in the oven for 30-45 minutes. To see if the cake is ready, put a toothpick of the cake. If it comes out clean, it\'s ready! If it comes out with batter on it, leave it in for a little longer.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(51,3,0,6,'Take the cake out of the pan and let it cool. In the meantime, make the a frosting of cream cheese, butter, sugar and vanilla.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(52,0,0,7,'Preheat oven to 450 degrees.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(53,1,1,7,'Combine all dry ingredients (including grated parmesan) on a plate and set aside.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(54,2,1,7,'Whip up the egg in a bowl until frothy and set aside.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(55,3,1,7,'Pat the 3-4 inch salmon sticks dry.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(56,4,1,7,'Dip the salmon sticks in the dry mixture, then into the beaten egg, then back in the dry mixture.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(57,5,1,7,'Place the sticks on a well-oiled baking sheet and then drizzle the fish sticks with olive oil.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(58,6,0,7,'Place the fish sticks in the oven and bake for 15-20 minutes until nice and golden brown.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(59,0,0,8,'Defrost the 8 empanada wraps (if purchased frozen).');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(60,1,0,8,'Wash and chop the 2 onions in small pieces.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(61,2,0,8,'Cut the bell pepper and dice into small cubes.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(62,3,0,8,'Heat the 4 tbsp butter in a small saucepan over medium heat.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(63,4,0,8,'Sauté the onions for 5 minutes or until golden brown.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(64,5,0,8,'Add the peppers and sauté for an additional 5 minutes.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(65,6,0,8,'Add the 1/4 cup of flour to the cooked vegetables and cook until bubbling (about 3 minutes) .');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(66,7,0,8,'Add the 1 cup milk slowly while whisking constantly, bring to a simmer, stirring constantly.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(67,8,0,8,'Add the 1/4 tsp paprika, 1 tsp nutmeg and salt and pepper.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(68,9,0,8,'Stir in the 2 cups corn and 1 tbsp parsley, return to a simmer and cook until corn is warmed through');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(69,10,0,8,'Scrape the filling into a bowl and cool completely before calling in the Little Chefs to fill the wrappers .');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(70,11,0,8,' Preheat the oven to 375° F.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(71,12,1,8,'Moisten the edges of 1 of the empanada wrappers with a fingertip dipped in warm water.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(72,13,1,8,'Put ⅓ cup of the filling on the center of the wrapper.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(73,14,1,8,'Bring the sides of the wrapper together to meet over the filling and pinch the sides together to seal and form a half-moon shape.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(74,15,1,8,'Lay the half-moon flat on the kitchen counter. Work your way around the edges, bringing about 1/2-inch of the bottom layer of dough up and over the top layer, to make a mini-pleat (half moon). Press as you go to seal the pleats.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(75,16,1,8,'Repeat with remaining wrappers and filling.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(76,17,1,8,'Grease two pans with a brush and olive oil.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(77,18,1,8,'Place the empanadas on the baking sheets and give the pans to the Big Chef');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(78,19,0,8,'Once the Little Chefs are done with their duties, bake until the empanadas are light golden brown, about 25 minutes.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(80,0,0,9,'Preheat grill to medium-high heat. ');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(81,1,0,9,'Cut 3 eggplants in half lengthwise, leaving stem ends intact. Give to Little Chef.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(82,2,1,9,'Mix the minced garlic and olive oil in a small bowl and give to Big Chef.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(83,3,1,9,'Combine oregano, basil, thyme, paprika and red pepper flakes in separate bowl and stir to mix.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(84,4,1,9,'Sprinkle cut side of eggplants with dried herb mixture and salt and pepper to taste. Give eggplants to Big Chef for cooking.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(85,5,0,9,'Lightly brush cut sides of eggplants with garlic-oil mixture and grill, cut side down, until lightly browned, about 3 minutes.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(86,6,0,9,'Lightly brush skin sides with oil mixture.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(87,7,0,9,'Flip eggplants and brush cut side again with remaining oil.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(88,8,0,9,'Continue cooking eggplants, cut side up until very tender, 6-8 minutes, or as needed.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(89,0,0,10,'Add 2 cups dulce de membrillo (quince paste), 1/4 cup pineapple and 1/4 cup raspberry jam to a small pot with 1 or 2 tablespoons of water, and cook at low heat, stirring frequently.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(90,1,0,10,'Remove pot from heat and let it cool.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(91,2,1,10,'Mix the dry ingredients (2 cups flour, 1/2 teaspoon salt, 1 tablespoon baking powder and 1/2 cup sugar) in a bowl using a whisk.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(92,3,1,10,'Cut the butter into small pieces and mix into ingredients until well blended.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(93,4,1,10,'Add 2 tbsp milk, egg and egg yolk to the dry mix. Make sure mix is not too wet or too crumbly; you can add an extra tablespoon of flour or milk to adjust.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(94,5,0,10,'Wrap dough in plastic and chill for about 30 minutes.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(95,6,0,10,'Preheat oven to 350° F.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(96,7,0,10,'Butter a 9 inch pie dish.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(97,8,1,10,'Roll about ¾ of the dough on a floured surface and make it a circle big enough to cover all of the pie dish, place in buttered dish.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(98,9,1,10,'Spread the filling prepared by Big Chef into the dish.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(99,10,0,10,'Roll the remaining dough into a circle and cut thin strips of dough.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(100,11,0,10,'Along with the Little Chef, make a lattice pattern over the top of the tart ');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(101,12,0,10,'Cook in the oven until golden brown (about 30 minutes).');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(102,0,0,12,'Open the cans of corn.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(103,1,0,12,'Wash and cut the 2 lettuce heads.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(104,2,0,12,'Cut the crust from the bread.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(105,3,1,12,'Mix the mayonnaise and the ketchup in a bowl.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(106,4,1,12,'Add a thin layer of the mayonnaise and ketchup mix on 10 of bread slices.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(107,5,1,12,'Add the corn to the rest of the ketchup mix.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(108,6,1,12,'Add a spoon of the corn with the mix, a slice of lettuce, a slice of cheese and cover with the clean 10 slices of bread.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(109,7,0,12,'After sandwich has been made push them down and make two diagonal cuts (from corner to corner) to create four triangles');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(110,0,0,13,'Put on a small amount of water to boil, and preheat oven to 350° F.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(111,1,1,13,'Measure out the 1 cup of oats,1 cup flour, 1 cup sugar, and 3/4 cup flaked coconut into a bowl and mix them together with a wooden spoon.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(112,2,1,13,'Measure out 1 teaspoon of baking soda into a mug or small bowl.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(113,3,0,13,'In a small saucepan on low heat, melt the butter and syrup together. Add the boiling water to the baking soda, mix, then add to the butter mixture.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(114,4,0,13,'Pour the butter mixture into the bowl of dry ingredients and mix with a spoon. When the mixture is cool enough to touch, you can let the Little Chef knead the mixture with his/her hands until everything is mixed in well.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(115,5,1,13,'Grease the cookie sheet with butter or cooking spray so the cookies don’t get stuck. ');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(116,6,1,13,'Make little balls out of the dough that are about 1 inch thick (about one heaped teaspoon each), and place them on the cookie sheets. Make sure you leave an inch or two between each one so they don’t get stuck to each other.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(117,7,0,13,'Put the cookies in the oven for 10-15 minutes, or until golden brown. ');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(118,0,1,14,'Wash all the carrots and celery, and then chop up the onion, celery, and carrots.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(119,1,1,14,'Grate ginger and lemon rind (from lemon).');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(120,2,0,14,'Sauté onion, garlic, celery, ginger, carrots, and lemon until onions are transparent.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(121,3,0,14,'Pour in broth, 1 tbsp vegemite, salt & pepper, and simmer until carrots are soft (about 20 minutes). ');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(122,4,0,14,'Pour into food processor or blender and blend until smooth. Add hot water until soup is desired consistency.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(123,0,0,15,'Peel and cut up the mango into cubes.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(124,1,0,15,'Peel and slice the onion thinly.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(125,2,0,15,'Grate the carrot and slice avocado.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(126,3,1,15,'Rinse the lettuce, shake the extra water off, and place in a big bowl');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(127,4,1,15,'Sprinkle the grated carrots and purple onion on top first.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(128,5,1,15,'Add the avocado and mango.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(129,6,1,15,'Sprinkle on the nuts, seeds, and raisins.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(130,0,0,16,'Place a wok or large skillet over high heat, and pour in 1 tablespoon vegetable oil. Cook the 1lb pork, stirring frequently, until no pink is showing.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(131,1,0,16,'Remove pork from pan and set aside. Drain grease from pan, leaving a thin coating.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(132,2,0,16,'Cook the 2 cloves of garlic and 1/2 cup of chopped onions in the same pan for 2 minutes. Stir in the cooked pork, 1/2 cup minced carrots, 1/2 cup sliced green onions, and 1/2 cup cabbage. Season with pepper, salt, garlic powder, and soy sauce.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(135,5,1,16,'Place three heaping tablespoons of the filling diagonally near one corner of each wrapper, leaving a 1 1/2 inch space at both ends. ');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(136,6,1,16,'Fold the side along the length of the filling over the filling, tuck in both ends, and roll neatly. Keep the roll tight as you assemble. ');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(137,7,1,16,'Moisten the other side of the wrapper with water to seal the edge. Cover the rolls with plastic wrap to retain moisture. ');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(138,8,1,16,'Slide 3 or 4 lumpia into the oil. Fry the rolls for 1 to 2 minutes, until all sides are golden brown. Drain on paper towels. ');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(139,0,0,17,'Peel and devein the prawns.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(140,1,0,17,'Let big chef finish their tasks.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(141,2,1,17,'Chop the tomato, eggplants, onions, and bitter melon.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(142,3,1,17,'Mince the garlic.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(143,4,1,17,'Trim the ends of okra and green beans.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(144,5,1,17,'Weigh the ingredients on the food scale.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(145,6,0,17,'Heat the oil in a large pot over medium heat.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(146,7,0,17,'Cook and stir the onion and garlic until translucent for about 5 minutes.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(147,8,0,17,'Add the pork and stir until browned for about 5 to 7 minutes.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(148,9,0,17,'Add the prawns and the shrimp paste and stir until the prawn turns orange.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(149,10,0,17,'Add the tomato and cover the pot for about 5 minutes.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(150,11,0,17,'Add all the vegetables and cover the pot, let it cook for about 10 minutes or until the vegetables are tender.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(151,0,0,18,'Heat oil in a wok or large skillet over medium heat. Saute onion and garlic until soft.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(152,1,0,18,'Stir in 2 cups chicken breast, 2 cups cabbage, 4 chopped carrots and 1 tsp soy sauce. Cook until cabbage begins to soften.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(153,2,1,18,'Place the rice noodles in a large bowl, and cover with warm water. When soft, drain, and set aside.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(154,3,1,18,'Toss in noodles, and cook until heated through, stirring constantly.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(155,0,0,19,'To cook the rice, add 4 cups of water and the 2 cups of rice to a large pot. Bring to a boil, stir, and cover the pot until the rice is cooked, about 20-25 minutes.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(156,1,0,19,'In a wok or large skillet, fry the 1 lb of pork until crisp. Remove the pieces of pork from the wok and drain on a plate covered with a paper towel. Leave about 2 Tbsp of the pork fat in the wok to cook the vegetables, pour off any extra.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(157,2,0,19,'Re-heat wok.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(158,3,1,19,'Chop 1 cup carrots, 1 tbsp onion, 1 tsp garlic, pepper, and cabbage.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(159,4,1,19,'Add the pepper, onion, cabbage, carrots and garlic to wok . Stir-fry 3 minutes.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(160,5,1,19,'Add pork. Cook 1 minute.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(161,6,1,19,'Add rice. Cook 1 minute.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(162,7,1,19,'Stir in peas and soy sauce. Cook 30 seconds.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(163,0,0,20,'Sauté the 3lb ribs, 2 tbsp garlic, 1 tbsp onions add salt to taste until brown.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(164,1,0,20,'Add the tomato and cook on medium heat for 40 minutes.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(165,2,0,20,'Add the 2 small taro and cook it for another 10 minutes.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(166,3,0,20,'Add the Daikon Radish and the Bok Choy and cook for another 10 minutes. ');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(167,4,1,20,'In a separate large pot fill with water.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(168,5,1,20,'Add the sinigang tamarind soup packet.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(169,0,1,21,'Pull tails off of the cooked shrimp, if purchased with tail-on.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(170,1,0,21,'Chop up the garlic.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(171,2,1,21,'Add oil, garlic, and seasoning to the shrimp.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(172,3,1,21,'Squeeze lemon juice onto the shrimp.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(173,4,0,21,'Cut the mushrooms in half.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(174,5,0,21,'Cut the bell peppers, onions, and zucchini.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(175,6,0,21,'Add the shrimp and vegetables to skewers and give to Big Chef.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(176,7,0,21,'Grill the skewers.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(203,0,0,25,'Soak macadamia nuts in a bowl of water for 2 hours.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(204,1,0,25,'Puree the macadamia into a paste, using a food processor or blender');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(205,2,0,25,'Chop up the basil.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(206,3,0,25,'Dice the clove of garlic');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(207,4,1,25,'Add the macadamia nut paste, tomato, basil, olive oil and garlic to a bowl and mix together.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(208,5,1,25,'Season the dip with salt, pepper, and lemon juice.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(209,6,0,25,'Cut bell pepper into strips, removing the seeds.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(210,7,1,25,'Place peppers on a plate and place the dip next to it.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(216,0,0,27,'Preheat oven to 350° F if using a metal pan, 325° F if using a glass pan.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(217,1,1,27,'Place flour, carob powder and salt in a large mixing bowl and thoroughly combine. Set aside.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(218,2,1,27,'In another large mixing bowl (or in the bowl of a stand mixer), combine the butter and sugar. Mix until fluffy and white. Add eggs and vanilla and beat until combined.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(219,3,1,27,'Add dry ingredients to wet ingredients and nuts (if using). Beat until wet and dry ingredients are just combined.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(220,4,1,27,'Grease the baking pan.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(221,5,0,27,'Scrape brownie mixture into prepared baking pan.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(222,6,0,27,'Bake for 25 minutes if using a metal pan, or 35 minutes if using a glass pan. ');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(243,0,0,30,'Cube potatoes for faster cooking. ');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(244,1,1,30,'Take the cubed potatoes and put them into a pot. Using a measuring cup, add enough water to completely cover the potatoes.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(245,2,0,30,'Cook potatoes after Little Chef has put them in the pot, until potatoes are soft when a fork is inserted.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(246,3,0,30,'While the potatoes are cooking, melt the butter in a large saute pan over medium heat.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(247,4,1,30,'Put garlic, onion and leeks into the saute pan. (Careful, the edges of the pan are hot!)');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(248,5,0,30,'Cook garlic and onion until it becomes a little soft, then add the leeks and saute until everything is just translucent.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(249,6,0,30,'Add the cream and stock and simmer for 10 minutes.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(250,7,0,30,'Drain the onion mixture, reserving the liquid. Set the onion mixture aside. Pour the reserved liquid back into the pan and cook until reduced by half, about 5 minutes.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(251,8,1,30,'Mix the onion mixture and potatoes together in a large bowl.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(252,9,0,30,'Once the Little Chef has mixed the onions and potatoes, return them to the pan with the cream and stock. ');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(253,10,0,30,'Season to taste with salt and pepper.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(254,0,1,31,'Rinse the endives in cold water, removing any damaged outer leaves.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(255,1,1,31,'Dry the endive leaves with paper towels. ');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(256,2,0,31,'After the Little chef has rinsed them, slice the endive leaves crosswise, about 1/2 inch thick.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(257,3,1,31,'Place the endives in a wide salad bowl after the Big Chef has cut them.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(258,4,0,31,'Quarter the apple and remove the core and then thinly slice. ');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(259,5,0,31,'Place in a bowl of water with 1 tablespoon lemon juice.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(260,6,1,31,'Add the walnuts, gruyère, parsley and tarragon.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(261,7,1,31,'Whisk together the vinegars, lemon juice, mustard, salt, and pepper. Whisk in the oils.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(262,8,0,31,'Drain the apple slices, dry on paper towels and add to the salad bowl.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(263,9,0,31,'Taste the dressing and adjust seasonings.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(264,0,0,32,'Cut off and fleshy inside scooped out with a teaspoon and left upside-down to drain for ½ hour.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(265,1,0,32,'Thaw the shrimp ahead of time.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(266,2,1,32,'Add the egg yolk and mustard to a medium-sized bowl.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(267,3,1,32,'With a whisk, very slowly add oil to the egg yolk mixture, starting with just a few drops at a time, until the mixture emulsifies.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(268,4,1,32,'Squeeze 1/2 lemons into the mixture.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(269,5,1,32,'Add the shrimp to the dressing and mix.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(270,6,0,32,'Spoon mixture into the tomatoes.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(271,NULL,0,33,'Warm the milk to 105-110ºF. Sprinkle the yeast over the warm milk and let it sit until it begins to get foamy. About 5 minutes.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(272,NULL,1,33,'In s bowl , stir together the butter, sugar, salt, and egg.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(273,NULL,1,33,'Pour in the milk/yeast mixture and the flour and make sure to mix at the same time.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(274,NULL,1,33,'Knead for about 5 minutes until it forms a soft sticky dough. The mixture will seem quite wet at first, but will form a smooth soft dough once kneaded. If the mixture is too wet after kneading, add more flour 1 TBSP at a time until a soft but sticky dough forms.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(275,NULL,0,33,'Pour in all of the sugar cube pieces. Mix until evenly incorporated.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(276,NULL,0,33,'Place the dough in a clean bowl with a towel or loose plastic wrap over it and allow to rise to double in size. About 1½ hours.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(277,NULL,0,33,'Break the dough into small pieces, about 2½ oz each. You should get about 10-12 pieces.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(278,NULL,0,33,'Heat your waffle iron up. Remember, sugar begins to burn at temps above 350ºF. If your waffle iron doesn\'t have a temp dial you may have to do trial and error to see the setting that will give you the best caramelization without burning. Most waffle irons heat much hotter than 350.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(279,NULL,0,33,'Remove the waffles from the iron with a spatula (caramelized sugar is really hot!) once the waffle is golden, puffed, and crispy.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(280,0,0,34,'Warm the milk to 105-110ºF. Sprinkle the yeast over the warm milk and let it sit until it begins to get foamy. About 5 minutes.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(281,1,1,34,'In the bowl , stir together the butter, sugar, salt, and egg.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(282,2,1,34,'Pour in the milk/yeast mixture and the flour and make sure to mix at the same time.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(283,3,1,34,'Knead for about 5 minutes until it forms a soft sticky dough. The mixture will seem quite wet at first, but will form a smooth soft dough once kneaded. If the mixture is too wet after kneading, add more flour 1 TBSP at a time until a soft but sticky dough forms.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(284,4,1,34,'Pour in all of the sugar cube pieces. Mix until evenly incorporated.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(285,5,0,34,'Place the dough in a clean bowl with a towel or loose plastic wrap over it and allow to rise to double in size. About 1½ hours.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(286,6,0,34,'Break the dough into small pieces, about 2½ oz each. You should get about 10-12 pieces.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(287,7,0,34,'Heat your waffle iron up. Remember, sugar begins to burn at temps above 350ºF. If your waffle iron doesn\\\'t have a temp dial you may have to do trial and error to see the setting that will give you the best caramelization without burning. Most waffle irons heat much hotter than 350.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(288,8,0,34,'Remove the waffles from the iron with a spatula (caramelized sugar is really hot!) once the waffle is golden, puffed, and crispy.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(289,0,1,35,'Use a vegetable peeler to peel the skin off the outside of the butternut squash.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(290,1,1,35,'Use a spoon to scoop out the seeds of the squash, after the Big Chef has cut it in half.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(291,2,1,35,'Tear the parsley into small pieces.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(292,3,0,35,'Melt butter in a pan over medium heat. Add the onion, tomato, garlic, and crushed red pepper. Cook, stirring occasionally, for about 10 minutes.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(293,4,0,35,'Add the squash, beef stock, salt, pepper, and sugar.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(294,5,1,35,'Help to add the squash and squash');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(295,6,0,35,'Bring to a boil. Lower the heat and simmer, covered for 30 min, or until the squash is soft and tender.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(296,7,1,35,'Puree the mixture in a blender. Once it is well blended, place it back in the pan and let it cook for another 5 minutes until smooth and consistent.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(297,0,0,36,'Preheat an oven to 350 degrees F (175 degrees C).');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(298,1,1,36,'Melt the sugar in a heavy saucepan over low heat, stirring constantly. Once the sugar becomes a golden brown syrup after about 10 minutes, pour it immediately into a round baking dish, swirling so that the syrup coats all sides of the dish. Set aside to cool.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(299,2,1,36,'Place the egg yolks into a blender and blend on medium for 5 minutes, then add condensed milk,  milk, and egg whites. Continue to blend until all ingredients are combined.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(300,3,1,36,'Pour egg mixture into the baking dish with sugar and cover with aluminum foil. ');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(301,4,0,36,'Line a roasting pan with a damp kitchen towel. Place baking dish on towel, inside roasting pan, and place roasting pan on oven rack. ');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(302,5,0,36,'Fill roasting pan with boiling water to reach halfway up the sides of the baking dish');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(303,6,0,36,'Bake in the preheated oven until a knife inserted 1 inch from the edge comes out clean, 45 to 50 minutes. The center of the flan will still be soft. ');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(312,0,1,38,'Chop spinach, tomatoes, garlic, and onion');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(313,1,1,38,'Mix all the ingredients together in a bowl, but the egg whites.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(314,2,1,38,'Beat the egg white to a firm consistency.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(315,3,1,38,'Gently with a spoon add the egg whites.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(316,4,0,38,'Once Little Chef has completed their duties, bake it in a buttered pan at 375 degrees for 30 minutes.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(317,0,0,39,'Boil yucca for 10-15 min.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(318,1,0,39,'Preheat oven to 425°.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(319,2,1,39,'If you are using a fresh yucca, peel the skin and the pinkish layer. Give to Big Chef to cut.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(320,3,0,39,'Cut yucca into thin strips or pieces (or any desired shape and size.)');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(321,4,1,39,'Once yucca has been cut, put them on a baking sheet and toss with the olive oil to coat.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(322,5,1,39,'Season with salt, pepper, and dried cilantro (or other herbs) and toss to make sure evenly distributed.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(323,6,0,39,'Put tray of yucca fries into oven. Fries are done when they are crispy on the outside and still soft on the inside.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(324,7,1,39,'After cooking, add additional salt, pepper and herbs, to taste.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(325,0,0,40,'Cut the beef.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(326,1,1,40,'Cut the garlic, onions and mangos.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(327,2,1,40,'Measure the other ingredients.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(328,3,0,40,'Heat some olive oil in a large stew pot over medium heat. Add the garlic, bay leaves, and crushed birdseye peppers and cook for about 3 minutes. Add the onions and cook until translucent, about 5 more minutes. Set aside.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(329,4,0,40,'Add more oil if needed, then cook the meat in a single layer until lightly browned on all sides, about 6 minutes. Repeat until all the meat has been cooked (you will likely have to do several batches). Add the onion mixture back in, stirring to combine.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(330,5,0,40,'Add enough water to just barely cover the meat. Bring to a boil, then lower to a simmer, cover, and cook for 1 1/2 hours, stirring occasionally.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(331,6,1,40,'After the meat has been simmered stir e. Cin half of the sliced mango, bulgur, saffron, allspice, salt, black pepper, and chipotlontinue simmering, covered, for another 45-60 minutes, until the meat is tender.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(332,7,1,40,'Remove the bay leaves, and stir in the remaining sliced mango.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(333,0,0,41,'Cook 2 cups of rice and fry chicken if not already cooked.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(334,1,1,41,'In a small bowl, beat 1 egg with water.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(335,2,0,41,'Melt 1 tbsp butter in a large skillet over medium low heat.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(336,3,0,41,'Add egg and leave flat for 1 to 2 minutes. Remove from skillet.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(337,4,1,41,'Cut the 1 onion and 2 tbsp ginger.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(338,5,1,41,'Cut the eggs into small pieces.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(339,6,0,41,'Heat 1 tbsp vegetable oil in same skillet; add onion and saute until soft.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(340,7,1,41,'Add rice, 2 tbsp soy sauce, 1 tsp pepper and chicken to the skillet.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(341,8,0,41,'Stir fry together for about 5 minutes, then stir in egg. ');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(342,0,0,42,'Cook 1 cup of rice using 1 1/3 cup of water in a rice steamer or pot.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(344,2,0,42,'After 5 minutes, take rice off the stove when finished. Leave there for 2 minutes before eating.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(345,0,0,43,'Cook 250g chicken and broth in a pan.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(346,1,1,43,'Chop the ginger, coriander, and green onions.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(347,2,1,43,'Mix cooked chicken, 1 tsp of  grated ginger, 2 tbsp of coriander, and 2 chopped green onions together.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(348,3,1,43,'Make dumplings by scooping mixture into wonton wrapper and seal.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(349,4,0,43,'Fry or steam the dumpling.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(350,0,0,44,'Rinse the chicken and pat dry. ');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(351,1,0,44,'Chop 2 scallions into thirds, lightly smashing the white sections. Peel and slice the ginger.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(352,2,1,44,'Measure 1 cups soy sauce, 2 tbsp sugar and 1 tsp five-spice powder.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(353,3,1,44,'Combine the dark soy sauce, sugar, five-spice powder, 2-3 slices of ginger and the 2 chopped scallions.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(354,4,1,44,'Place the chicken in a large resealable bag and add half the soy sauce marinade. Reserve the remaining half. Seal the bag and squish the bag around to fully coat the chicken with the marinade.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(355,5,0,44,'Marinate the chicken in the refrigerator for at least 4 hours (overnight if possible), turning the bag occasionally so that the chicken is evenly marinated.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(356,6,0,44,'After the chicken has marinated, preheat the oven to 450 degrees Fahrenheit.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(357,7,0,44,'Take the remaining 4 scallions and tie into a knot. Give to Little Chef, along with remaining ginger slices.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(358,8,1,44,'After the chicken has marinated, take the knotted scallions and the remaining sliced ginger and place it under the skin of the chicken.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(359,9,0,44,'Reduce the heat to 375 degrees Fahrenheit (190 degrees Celsius, gas mark 5). Roast the chicken, basting frequently with the reserved marinade, for another hour or until it is cooked. To check for doneness, pierce the chicken in the thickest part of the thigh. The juices should run clear. If using a meat thermometer, the temperature should read 170 degrees Fahrenheit (76 degrees Celsius) in the thickest part of the thigh.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(360,0,0,46,'Preheat the oven to 325° fahrenheit.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(361,1,1,46,'Zest the 2 oranges, make sure to get as much as possible.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(362,2,1,46,'In a big bowl, pour the flour (32 oz.) and the 2 tsp. baking powder and mix with a spoon.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(363,3,1,46,'In a second bowl pour 8.5 oz. olive oil and corn oil, 14 oz. sugar, 2 tsp. cinnamon, 1 tsp ground cloves and the grinded orange peel and mix well.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(364,4,1,46,'In a third bowl pour the orange juice and add the baking soda. (Notice how the mix will rise so make sure to use a big bowl).');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(365,5,1,46,'Pour the juice mix into the oil mix, then use a whisk to stir well.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(366,6,1,46,'Gradually add the flour mix, combining slowly until an oily and soft dough is formed. ');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(367,7,1,46,'Pinch a portion of dough off about the size of a walnut. Shape in your palms into a smooth oblong shape, and place on cookie sheet.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(368,8,0,46,'Bake the cookie for 25 minutes, until crispy.  Let cool down completely before adding the syrup.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(369,9,0,46,'While the cookies bake prepare the syrup. Have chef koochooloo measure ingredients into a pot, and cook on medium heat.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(370,10,1,46,'Put all the syrup ingredients in a pot.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(371,11,0,46,'When the mix starts to make bubbles, continue cooking for another 3 minutes.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(372,12,0,46,'Remove it from the stove and pour it over the melomakarona.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(373,0,1,47,'Peel the potatoes and give them to the Big Chef.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(374,1,0,47,'In a large pot of boiling water, add potatoes (from Little Chef) and boil until easily pierced with a fork. Allow to cool and then cut into ½-inch pieces and give to Little Chef.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(375,2,1,47,'Mash the potatoes once they have been cut by the Big Chef.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(376,3,1,47,'Mix the fish roe, shallots, lemon juice, and olive oil into the potatoes.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(377,4,1,47,'Sprinkle the olives and cilantro on top of the fish spread as a garnish.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(378,0,1,48,'Wash the tomatoes, the peppers and the zucchini thoroughly ');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(379,1,1,48,'Wash the parsley and the dill thoroughly');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(380,2,1,48,'Cut the top of the bell peppers while the Big Chef cuts the top of the tomatoes');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(381,3,0,48,'Slice off the top of the tomatoes');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(382,4,0,48,'Use a spoon remove the flesh of the tomatoes and keep it in a bowl. Try to leave the vegetables as thin as possible, leaving just a little of the flesh, but be careful not to poke through their skin');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(383,5,1,48,'Remove the seeds and membranes of the bell peppers and discard');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(384,6,0,48,'Arrange the hollowed peppers and tomatoes in a baking dish that fits them about right with the hollowed sides facing upward');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(385,7,0,48,'Add the tomato flesh to a food processor and puree.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(386,8,0,48,'Add the onions to a food processor and puree.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(387,9,0,48,'While the Little Chef is filling the vegetables, preheat the oven at 360 F.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(388,10,1,48,'Chop the zucchini in small cubes.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(389,11,0,48,'Chop finely the parsley and the dill.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(390,12,1,48,'After the Big Chef has pureed the tomato flesh and the onions, add them in a bowl along with the chopped zucchini, the chopped parsley, the chopped dill and the tomato sauce and the olive oil');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(391,13,1,48,'Add the rice to the mix.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(392,14,1,48,'Add salt and pepper and mix well. ');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(393,15,1,48,'Use a spoon to fill the tomatoes and the peppers with the mix');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(394,16,0,48,'After all vegetables are filled and in case any filling mix is left add it to the baking dish ');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(395,17,0,48,'Pour 2 cups water in the baking dish.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(396,18,0,48,'Sprinkle 1-2 tbsp olive oil over the vegetables.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(397,19,0,48,'Bake the stuffed vegetables for 45 min.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(398,0,0,49,'Wash and drain the chopped spinach very well. If using frozen spinach, thaw completely and squeeze out excess water. Spinach should be dry.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(399,1,0,49,'Heat the half cup of olive oil in a deep saute pan or large dutch oven. Saute the onions and green onions until tender. Add the spinach, parsley, and dill and cook for 5 to 10 minutes until the spinach is wilted and heated through. Add the nutmeg and season with salt and pepper.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(400,2,0,49,'Remove from heat and set the spinach aside to cool.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(401,3,1,49,'In a large mixing bowl, combine the feta, eggs, and ricotta (cottage) cheese. Add the cooled spinach mixture and mix until combined.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(402,4,1,49,'Combine the melted butter with the olive oil in a bowl. Using a pastry brush, lightly grease two 9 x 12 rectangular pans.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(403,5,0,49,'Carefully remove the Phyllo roll from the plastic sleeve. Most packages come in 12 x 18 inch sheets when opened fully. Using a scissor or sharp knife, cut the sheets in half to make two stacks of 9x12 inch sheets. To prevent drying, cover one stack with wax paper and a damp paper towel while working with the other.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(404,6,1,49,'Layer about 10 sheets on the bottom of the pan making sure to brush each sheet with the butter/olive oil mixture. ');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(405,7,1,49,'After the 10 layers add half of the spinach mixture in an even layer and press with a spatula to flatten.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(406,8,1,49,'Layer another 10 sheets on top of the spinach mixture making sure to brush well with butter/olive oil mixture. ');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(407,9,1,49,'Repeat the process with the second pan.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(408,10,0,49,'Before baking, score the top layer of phyllo (making sure not to puncture filling layer) to enable easier cutting of pieces later. ');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(409,11,0,49,'Bake for 20 to 25 minutes.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(410,0,1,50,'In a large mixing bowl, combine all of the ingredients and mix well.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(411,1,1,50,'Cover with plastic wrap and allow mixture to sit in the refrigerator for at least an hour.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(412,2,0,50,'Preheat oven to 375 degrees F. ');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(413,3,0,50,'Line a baking sheet with aluminum foil and lightly grease with either the cooking spray or brushed on olive oil.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(414,4,1,50,'Roll the meat mixture into balls about the size of a walnut.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(415,5,1,50,'Place the balls on a greased baking sheet about an inch apart. ');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(416,6,1,50,'Bake in 375 degree oven for about 40 minutes, turning the meatballs midway through the cooking time. ');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(417,0,1,51,'Peel and slice the carrots.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(418,1,0,51,'Place carrots in pot, cover with water, and bring to a boil. Simmer 30 minutes, or until tender. Drain.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(419,2,0,51,'Preheat oven to 375F. Coat 6 crème brûlée dishes or 1 1-qt. gratin dish with cooking spray. ');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(420,3,1,51,'With adult supervision purée carrots in food processor until smooth. Add eggs, bread crumbs, onions, salt, pepper and cumin, and pulse until combined.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(421,4,1,51,'Spread mixture in prepared dishes, and hand to big chef.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(446,0,0,55,'Preheat the oven to 325° F. ');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(460,0,1,56,'In a mug, dissolve 1 packet (instant) yeast in 250 ml water.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(461,1,1,56,'In a bowl, combine 300 g sifted flour with 1/2 tsp salt.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(462,2,1,56,'Add the yeast and water to it and stir thoroughly (if it is very sticky, add a little bit more flour).');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(463,3,0,56,'Work the dough with a wooden spoon or with your hands until the dough is smooth and no longer sticks to the bowl. ');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(464,4,0,56,'Leave the dough in the bowl, cover with a clean cloth and let it rise for 30-40 minutes or until it has doubled in size.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(465,5,0,56,'Let the pieces rest for another 30 minutes on the floured surface.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(466,6,1,56,'Once it is rested, carefully tip out the dough onto a floured surface, stretch it out into a square and cut out about 10 cm (3,93 inch) round shapes with a big glass (a big cookie cutter also works). ');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(467,7,1,56,'Stretch out each piece with your fingers into a round shape with the centre being thinner than the edges. ');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(468,8,0,56,'In a saucepan, heat sunflower oil. Place the lángos into the hot oil, fry it on one side until golden brown then turn. Repeat with the remaining lángos dough.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(469,0,0,57,'Sprout the Green Gram Moong (if not making green sprouts, skip to step 6.)');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(470,1,0,57,'Cover and soak the beans with 3 inches of water overnight.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(471,2,0,57,'After the beans have soaked overnight or for 8 hours, strain them in a large colander and rinse under cold running water.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(472,3,0,57,'Cover the colander with a kitchen towel and set aside for another day.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(473,4,0,57,'Every 8-10 hours rinse the legumes and let the water run out, then cover again with the kitchen towel and reset. Once you see tiny little white shoots developing, allow for them to sit another day, continuing to rinse and drain, until the shoots are bigger.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(474,5,0,57,'Finely chop the 2 small or medium onions.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(475,6,0,57,'Boil the 3 potatoes (optional).');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(476,7,1,57,'Peel and chop the potatoes in small pieces.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(477,8,1,57,'Chop the 4 medium size tomatoes and 1 bunch of coriander leaves into small pieces.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(478,9,1,57,'Mix all the chopped ingredients with the sprouts in a bowl.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(479,10,1,57,'Season it with rock or black salt as desired.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(480,11,1,57,'Add a few drops of lemon juice and 1/2-1 tsp of chaat masala (optional).');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(481,0,1,58,'Clean and soak the lentils.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(482,1,1,58,'Drain the soaked lentils, leave it in strainer for sometime so that all the water would be strained.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(483,2,1,58,'Grate carrot.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(484,3,1,58,'In a mixing bowl add 1/3 cup strained Moong Dal, 1/4 cup grated coconut, 2 tbsp chopped coriander and 1 grated carrot.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(485,4,0,58,'Just before serving add salt & lemon juice and mix well.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(486,5,0,58,'Tempering the salad (In a small pan heat oil and crackle mustard seeds. Now, add Urad Dal and fry until it turns golden brown. Add curry leaves and asafetida and fry for few more seconds.)');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(487,0,0,59,'Boil beetroots and potatoes.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(488,1,0,59,'In a pan add little oil and sauté onions, beans and carrots till they are cooked.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(489,2,1,59,'Peel and mash the potatoes in a big bowl.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(490,3,1,59,'Mash the beetroot and peas in the bowl as well.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(491,4,1,59,'Add the sautéed onions, beans and carrots in the bowl.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(492,5,1,59,'Chop cilantro in small pieces and add them in the bowl');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(493,6,1,59,'Mash all these veggies together');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(494,7,1,59,'Add salt, red chili powder, pepper, cumin powder and salt to the mash and mix it well.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(495,8,1,59,'Place small quantity of mash between 2 bread slices and put the sandwich on the grill.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(496,0,0,60,'Peel and cut mango if fresh; thaw mango slightly if frozen.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(497,1,1,60,'Measure 2 cups of the plain yogurt and add to blender.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(498,2,1,60,'Measure 1/2 cup of milk and pour into blender.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(499,3,1,60,'Measure 6 tsps of sugar or agave nectar and pour in blender.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(500,4,1,60,'Measure 1 cup of ice and add into blender.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(501,5,0,60,'Once Little Chef has completed their duties, blend on smoothie mode or until well blended.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(503,0,0,1,'Preheat oven to 350 degrees F (175 degrees C). Cut tops off of the peppers.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(504,1,1,1,'Remove seeds from inside of the peppers');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(505,2,1,1,'Chop onions up, but makes sure to separate 1 onion for stuffing and 2 for the sauce.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(506,3,1,1,'Measure out the ingredients for sauce and hand to big chef. ');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(507,4,0,1,'In a skillet over medium heat, saute 1 chopped onion until tender. ');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(508,5,0,1,'Heat oil in a large saucepan over medium heat, Saute 2 chopped onions until soft and translucent. Stir in tomatoes. Season with 3 whole peppercorns, 3 whole allspice berries, 1/2 tsp ground allspice, 1 tsp oregano, 1 tsp marjoram, salt and pepper (personal preference). Cook on medium heat for 15 to 20 minutes. Pour over peppers in roasting pan.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(509,6,1,1,'In a large bowl, combine the ground beef, the sauteed onion, 1 cup cooked rice and 1 egg. Season with 1 tbsp parsley, salt and pepper. Mix well, and stuff into peppers. Place in a large roasting pan.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(510,7,0,1,'Cover, and bake in preheated oven for 1 1/2 hours. Remove peppers, and transfer sauce to a blender.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(511,8,1,1,'With supervision puree sauce until smooth, and pour back into pan.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(512,9,1,1,'After sauce is pureed put pan back in oven to reheat.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(514,0,1,61,'In a small bowl, combine the yeast with the water. Set aside.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(515,1,1,61,'In a large bowl, cream together 1/4 cup butter, 1/4 cup shortening, and 1/4 cup sugar until the mixture is light and fluffy. Mix in the 2 egg yolks, 2/3 cup milk, and 1 tsp salt, combining well.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(516,2,1,61,'Stir in the dissolved yeast and 4 cups flour, and mix until the ingredients are thoroughly blended into a soft dough.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(517,3,0,61,'Cover the dough with a towel, and set the dough aside to rise to about double in size, approximately 1 to 1 1/2 hours.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(518,4,0,61,'After chef koochooloo has made balls set them aside to double in size again, about 45 minutes to one hour.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(519,5,1,61,'Pinch off pieces of dough about the size of a golf ball, flatten the balls slightly, and transfer them to the baking sheet.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(524,10,0,61,'Preheat oven to 425°.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(525,11,1,61,'After fluffed up gently indent the top of the dough with your thumb, fairly deep.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(526,0,0,62,'Preheat oven to 400°F , butter a baking dish or line with baking paper then set aside.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(527,1,0,62,'Cut the bread into large cubes or slice into the desired shape.( For the bottom layer use long slices,  and for the top used cubed bread.) ');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(528,2,1,62,'In the large bowl lightly beat 2 eggs, 3 tbsp milk, 2 tbsp sugar and some of the spices until well combined.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(529,3,1,62,'Mix the bread cubes/slices into the bowl making sure that they are all well coated with the egg mixture.Keep in bowl and set aside.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(530,4,1,62,'Peel and grate the apples into the bowl.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(531,5,1,62,'Sprinkle the spices and raisins into the grated apples. Using your hands or a spoon mix the spices into the apples until everything is well combined.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(532,6,1,62,'(Now you will need the soaked bread and the baking dish) Make a bottom layer on the baking dish with some of the soaked bread, but you’ll need to reserve enough bread for the top layer.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(533,7,1,62,'Once the bottom bread layer is in place evenly spread the grated apple mixture on top. Make sure to keep a little to top it off.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(534,8,1,62,'Lastly cover the apple layer with another layer of soaked bread cubes.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(535,9,0,62,'Place in the center of preheated oven and bake for 25 minutes allowing for both the top bread layer to brown and the bottom layer to have solidify.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(536,0,1,63,'Mash apricots, stir in 2 tablespoons of sugar and let filling cool. Set aside.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(537,1,1,63,'Cream butter and cream cheese until fluffy; add ¾ cup sugar and beat well.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(538,2,1,63,'Add flour and mix well.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(539,3,0,63,'Combine apricots and water in a heavy saucepan and cook covered over medium heat until apricots are soft, then cook for 10 minutes uncovered. ');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(540,4,1,63,'Shape dough into a ball and chill for 1 hour.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(541,5,1,63,'Turn dough onto well-floured surface, working with only half the dough. Roll into 1/8 inch thickness and cut into 2 inch squares.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(542,6,1,63,'Spoon 1/2 teaspoon of apricot filling into the center of the square. Bring four corners to center, pinching to seal.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(543,7,1,63,'Place on baking sheet.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(544,8,0,63,'Bake at 400° F for about 15 minutes.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(545,0,1,64,'Wash your lentil very well.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(546,1,0,64,'Peel all vegetables (carrot, tomato, potato, zucchini, onion, and garlic cloves) and chop into medium size pieces.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(547,2,1,64,'Fill up half a medium deep pan with water. Add lentils, carrot, tomato, potato and garlic.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(548,3,1,64,'Open the can of chicken broth using safe can opener.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(549,4,1,64,'With the help of a grownup mix all cooked veggies and lentils together using a hand mixer');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(550,5,1,64,'Add butter over strained vegetable mix, and season using salt, pepper and cumin.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(551,6,0,64,'Boil over high heat for 20-25 minutes. ');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(552,0,0,65,'Peel and wash the shrimp, then pat dry.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(553,1,0,65,'Place shrimp in large self sealing plastic bag.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(554,2,0,65,'Prepare the couscous according to the package, substitute the chicken broth for water.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(555,3,1,65,'Add a tsp. of oil, the garlic, tarragon, thyme, and Old Bay seasoning in the bag, and then seal it.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(556,4,1,65,'Gently turn and “massage” the bag to coat the shrimp. Refrigerate for 1 hour.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(557,5,1,65,'Combine lemon juice and butter (1/4 cups) in a bowl, set aside.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(558,6,1,65,'Divide the spinach among individual bowls. ');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(559,7,1,65,'Spoon the couscous onto the spinach. ');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(560,8,0,65,'Sauté the shrimp in a large skillet with the marinade over medium heat for 3 to 5 minutes.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(561,9,0,65,'Pour half of the butter over the shrimp.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(562,10,1,65,'Arrange the shrimp and the couscous.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(563,11,1,65,'Pour remaining lemon butter over everything.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(564,12,1,65,'Sprinkle with shredded parmesan cheese. ');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(565,0,1,66,'Peel and cube the eggplant, then soak in salted water for 15 minutes.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(566,1,1,66,'Rinse and drain the eggplant.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(567,2,1,66,'Cut and dice the onion.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(568,3,0,66,'Cut lamb into 1-inch squares and sauté in butter until brown on all sides.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(569,4,1,66,'Add onion and eggplant, seasonings and consommé to the casserole.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(570,5,0,66,'Fry onion in the skillet, until golden brown.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(571,6,0,66,'Brown raw rice in a dry skillet and blend with canned tomatoes (2 cups) and their juice.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(572,7,0,66,'Bake until lamb is tender, about 90 minutes');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(573,8,1,66,'Pour over the contents of the casserole');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(574,0,1,67,'Slice peppers (3 sweet peppers green, yelow, red) in half from stem to bottom and remove stem');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(575,1,1,67,'Remove seeds and membranes, set aside');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(576,2,0,67,'Melt butter (2 Tbsp.) in skillet.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(577,3,0,67,'Add onion, corn, sausage, breaking it apart with a spoon or spatula, and cook until the sausage is no longer pink.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(578,4,0,67,'Add crumbled corn bread (2 cups) or a little water if the mixture seems too dry');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(579,5,1,67,'Divide filling among peppers evenly and top with shredded cheese. ');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(580,6,0,67,'Heat oven to 350F.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(581,7,0,67,'Bake peppers at 350F, covered, for about 30 minutes, or until the cheese is melted and peppers are tender. ');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(587,0,1,69,'Peel the potatoes.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(588,1,1,69,'Chop the parsley.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(589,2,0,69,'After Little Chef has peeled the potatoes, boil them in a pot of boiling salted water.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(590,3,1,69,'After the Big Chef has boiled the potatoes, combine potatoes with salt, and melted butter.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(591,4,0,69,'Melt the butter in a pan, add the cooked potatoes and then sprinkle with the salt.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(592,5,0,69,'Boil bratwurst, frankfurters, or knackwurst sausages for five minutes. ');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(593,6,1,69,'Sprinkle with the fresh chopped parsley, shake the pan to distribute the parsley and serve immediately.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(594,7,1,69,'Add the sausages.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(595,0,0,70,'Cook spaetzle pasta.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(596,1,1,70,'Cover baking sheet pan with parchment paper. ');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(597,2,1,70,'Coat a medium size casserole dish with cooking spray or butter.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(598,3,1,70,'Combine squash, shallots, spice blend, oil, salt and pepper. Toss to coat well, then scatter into a single layer on parchment covered sheet pan.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(599,4,1,70,'Whisk eggs. Stir in cheeses, parsley, and lemon zest. Add salt and pepper to taste. Fold into spaetzle mixture in the casserole.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(600,5,0,70,'After Little Chef has spread squash mixture on to baking sheet, put in oven and bake for 30 minutes, take out and out to side.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(601,6,1,70,'Add in the cooked spaetzle to casserole dish.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(602,7,0,70,'Preheat oven to 400 degrees F.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(603,8,0,70,'Bake the spaetzle mixture until browned on top.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(604,0,0,71,'Bring water to a boil.  Cook the 1lb elbow macaroni according to package instructions. Drain and set aside.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(605,1,1,71,'Mince the 3/4 cups of carrots.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(606,2,1,71,'Grate the 1 cup sharp cheddar cheese.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(607,3,0,71,'In a large mixing bowl, combine the 3/4 cups mayonnaise, 1/4 tsp garlic powder, 3 tablespoons of pineapple juice from the canned tidbits, salt, and ground black pepper.  Mix well and then taste.  Adjust the taste if necessary.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(608,4,0,71,'Add the carrots, macaroni, corn, onion, raisins, and pineapple tidbits in the mixing bowl.  Toss thoroughly until the ingredients are well distributed.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(609,5,0,71,'Add the cheese.  Gently toss.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(610,6,1,71,'Cover the mixing bowl with a cling wrap and refrigerate for about an hour.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(611,0,1,72,'Blot the asparagus (5 stalks) dry with a clean kitchen towel, then cut into 1/2-inch slices with child-safe scissors.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(612,1,0,72,'Fill a large skillet with 1 inch of hot water and bring to a boil. Add asparagus. Partially cover and cook the asparagus until tender-crisp, about 3 minutes. Drain; refresh under cold water.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(613,2,1,72,'Coat six 10-ounce ramekins with butter and add fine bread crumbs.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(614,3,1,72,'Place ramekins on a large rimmed baking sheet.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(615,4,0,72,'Position rack on lowest level of oven; preheat to 375°F.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(616,5,0,72,'Heat 3/4 cups milk in a small saucepan over medium heat until hot.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(617,6,1,72,'Get the milk mixture from the Big Chef and whisk in 1/4 teaspoon each of salt, pepper and nutmeg.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(618,7,1,72,'Whisk in 4 egg yolks, one at a time, and truffle oil, and stir in the asparagus and cheese.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(619,8,1,72,'Place 8 egg whites in a large bowl. Beat with an electric mixer, slowly increasing the speed, until they begin to foam. Add the remaining 1/4 teaspoon salt and continue to beat until the whites hold their shape.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(620,9,1,72,'Using a rubber spatula, gently stir one-third of the whites into the egg yolk mixture to lighten it. Gently fold in the remaining egg whites just until blended. Divide the soufflé mixture among the prepared ramekins, filling them almost to the top.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(621,10,0,72,'Melt butter in a medium saucepan over medium-low heat.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(622,11,0,72,'Whisk in flour and cook, whisking often, for 2 minutes.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(623,12,0,72,'Turn off heat and slowly whisk in hot milk.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(624,13,0,72,'Return the heat to medium-low and continue whisking until the mixture is thickened, 3 to 4 minutes. Remove from heat and put in a large bowl to give to Little Chefs.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(625,14,0,72,'Once the Little Chefs have finished their duties, bake the soufflés on the bottom rack until puffy and golden, about 20 to 25 minutes.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(626,0,0,73,'Slice fennel into very thin pieces.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(627,1,0,73,'Cut off the tops and the bottoms of the grapefruit and oranges. Slice 	off the peel and the white pith, and cut in between the white membranes to yield individual segments. Put in a large bowl and stir in the sugar, vinegar, olive oil and pepper.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(628,2,1,73,'Mix the 5 oranges, 4 grapefruit, 1 tsp. sugar, 3 tbsp. vinegar, 1/2 cup olive oil and pepper together.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(629,3,0,73,'Drain the fennel and dry well.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(630,4,1,73,'Take the fennel add salt and pepper, and mix with the citrus ingredients.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(631,5,0,73,'Arrange the arugula on a serving platter.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(632,6,1,73,'Place the mixed ingredients on the arugula and top with lemon zest, dill, parsley and mint.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(633,0,1,74,'In a small bowl, beat the 3 eggs, 2/3 cup milk, 2 Tbsp. melted butter, and 1/4 tsp. salt until well combined-about 20 seconds');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(634,1,1,74,'Sift the 1/3 cup flour into a medium size bowl or a 2 quart measuring cup, then add the liquid mixture, beating until well combined-about 1 minute.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(635,2,0,74,'After chef koochooloo has finished cover mixture loosely with plastic wrap; let stand for at least 1 hour refrigerated or at room temperature. ');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(636,3,0,74,'Brush butter on the bottom of a crepe pan or skillet.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(637,4,0,74,'Stir the batter well, pour 2 tablespoons of batter into the skillet, and quickly tip it back and forth so that the batter evenly coats the bottom.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(638,5,0,74,'Cook until the crepe edges are golden brown-about 1 minute. Slide a spatula under the crepe, flip it over, and cook for about 30 seconds. ');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(639,6,0,74,'Repeat with the remaining batter.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(640,0,1,75,'Trim the cauliflower & break into small florets.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(641,1,0,75,'Pre-heat oven to 450°F.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(642,2,0,75,'Boil in salted water for 10-15 minutes or until just tender.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(643,3,0,75,'Drain in a colander and then place in a buttered ovenproof baking dish.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(644,4,0,75,'Heat and whisking with a wire whisk continuously until the sauce thickens, boils and is smooth.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(645,5,1,75,'Add the 1.33 ml. milk, 3 Tbsp. flour and 1/2 cup butter to a saucepan.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(646,6,1,75,'Add three-quarters of the grated cheese, mustard, a pinch of nutmeg and seasoning.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(647,7,0,75,'Allow to simmer for a further 2 minutes. Allow chef koochooloo to add cheese.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(648,8,0,75,'Cook for further minute stirring well.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(649,9,0,75,'Pour the sauce over the cauliflower.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(650,10,1,75,'After sauce has been poured over cauliflower add the remaining cheese, and breadcrumbs, over the top.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(651,11,0,75,'Bake for about 15 to 25 minutes until golden brown and bubbling.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(652,0,1,76,'In a large skillet, melt the butter over medium heat; add the chicken and cook for 6 to 8 minutes per side, or until browned on both sides. ');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(653,1,1,76,'Measure and add the remaining ingredients.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(654,2,0,76,'Cover, reduce heat to low. Cook for 5 to 10 minutes, or until the chicken is fork-tender and no pink remains.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(655,0,0,77,'Wash and cut the rhubarb in 3 cm (2 inches) chunks and place in a sieve sitting on a bowl. ');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(656,1,0,77,'Butter and flour a 23 cm tart tin, cutting out the excess pastry around the rim.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(657,2,0,77,'Preheat the oven at 360 degrees F');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(658,3,0,77,'Place the drained rhubarb in a fry pan and sauté on high heat for a few minutes until just softened.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(659,4,0,77,'Mix the eggs, cream, sugar, ground almond and almond essence for 20 seconds on speed 3 (medium speed).');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(660,5,1,77,'Sprinkle with the 1/2 cup of sugar and let it soften for 2 hours so the water drains out. Remove the pre-made pastry from the fridge while you are waiting.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(661,6,1,77,'Roll the pastry and place in the tin. ');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(662,7,1,77,'Drain the rhubarb (save the juice!). ');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(663,8,1,77,'Pour over the rhubarb chunks and bake for 30 minutes.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(664,9,1,77,'Reduce the reserved rhubarb juice in a small saucepan and brush over the tart while it’s still hot. Let the tart cool down before taking out of the tin.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(665,0,0,78,'Cut the pomegranate in half and remove 1/2 cup seeds. Keep the seeds and throw away the rest.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(666,1,0,78,'Chop 2 cups pineapple.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(667,2,1,78,'Carefully whisk 2 1/2 cups yogurt or curds.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(668,3,1,78,'In a large bowl, combine the pineapple, yogurt, and pomegranate.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(669,4,1,78,'Add 2 tsp sugar, 1 tsp cayenne pepper, and 2 tsp cumin powder. Add salt to taste.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(670,5,0,78,'Finely chop the cilantro leaves.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(671,6,1,78,'Sprinkle with cilantro leaves.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(672,7,0,78,'Refrigerate for at least 1 hour before serving.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(673,0,0,79,'Preheat oven to 425 degrees F.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(674,1,0,79,'Cut onion into medium pieces.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(675,2,1,79,'In a small bowl, whisk together 1/4 cup olive oil, 3/4 tsp coriander powder, 1/2 tsp turmeric, 1/2 tsp salt and pepper.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(676,3,1,79,'Brush each potato slice with a layer of the spiced oil.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(677,4,1,79,'Add 1 lb. Potatoes and onions in baking dish.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(678,5,0,79,'Bake for 30 minutes or until potatoes are golden brown.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(679,6,0,79,'Warm up Indian Bread (Rotis) to go with the Potatoes.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(680,0,0,80,'Grate ¼ cup of  onion.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(681,1,1,80,'Wash potatoes.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(682,2,1,80,'Beat 2 eggs.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(683,3,1,80,'Crush crackers.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(684,4,1,80,'Measure the salt, 1/4 tsp black pepper, and 1/2 cup vegetable oil.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(685,5,0,80,'Shred 2 cups of potato. ');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(686,6,1,80,'Mix potato, onion, eggs, crackers, salt, and pepper together in a large bowl');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(687,7,0,80,'Pour enough vegetable oil into a skillet to fill about 1/2-inch deep; heat over medium-high heat. Drop spoonfuls of the potato mixture, first pressing potato mixture against the side of the bowl to remove excess liquid, into the hot oil; slightly flatten the latkes into the oil with the back of your spoon so they are evenly thick.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(688,8,0,80,'Cook in hot oil until browned and crisp, 3 to 5 minutes per side. ');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(689,9,0,80,'Drain latkes on a plate lined with a paper towel.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(690,0,0,81,'Place prunes and apricots into two separate pots filled with water and bring to a rolling boil over high heat. Cook the fruit, uncovered, stirring occasionally, until the fruit is tender, about 15 minutes. Drain fruit in a colander and mash together in a bowl using a fork. Set aside.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(691,1,0,81,'Chop almonds and apricots in a food processor, with a few dashes of lemon juice. Chop pecans and prunes in a food processor, with a few dashes of lemon juice.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(692,2,0,81,'Preheat an oven to 350 degrees F (175 degrees C).');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(693,3,0,81,'Mix 6 oz prune and 3 oz pecan pieces in a bowl. Mix 6 oz apricot and 3 oz almond pieces in another  bowl.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(694,4,1,81,'Whisk eggs, 1 cup sugar, oil, 1 tbsp lemon zest, and orange zest together in a bowl and set aside.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(695,5,1,81,'Mix flour, almonds, baking powder, and salt. Blend & cut in butter until mixture is crumbly. Combine egg, lemon juice, and water only to incorporate.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(696,6,1,81,'Roll out dough to about ¼ inch in thickness on a lightly floured surface. Cut circles out using a 3 inch cookie cutter or the rim of a large drinking glass.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(697,7,1,81,'Place a teaspoon of this filling in the center of a circle of cookie dough. ');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(698,8,1,81,'To fold the cookies, first take the left side of the circle and fold it towards the center to make a flap that covers the left third of the circle. Then, take the right side of the circle and fold it towards the center, overlapping the upper part of the left side flap to create a triangular tip at the top of the circle. A small triangle of filling should still be visible in the center. Grasp the bottom part of the circle and fold it upward to create a third flap and complete the triangle. ');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(699,9,1,81,'Repeat with the remaining cookies.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(700,10,0,81,'Bake in the preheated oven until golden brown, about 20 minutes. Cool in the pans for 10 minutes before removing to cool completely on a wire rack.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(702,0,0,82,'Cook the couscous according to the package directions. Drain the cooked couscous and rinse with cold water to stop the cooking process. Help the Little Chef with their duties.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(703,1,1,82,'Finely chop 2 tbsp shallot.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(704,2,1,82,'Rinse the vegetables and herbs.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(705,3,1,82,'Cut the cucumber in half and using a spoon, scoop out the seeds and discard. Finely chop the cucumber, discarding the ends.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(706,4,1,82,'Squeeze the lemons and measure out 2 tablespoons of lemon juice. ');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(707,5,1,82,'Cut the tomatoes in half.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(708,6,1,82,'Finely chop the parsley and mint. ');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(709,7,1,82,'In a large bowl, add the ½ cup olive oil, chopped shallot, and lemon juice. Whisk to combine.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(710,8,1,82,'Add in the cooked couscous, cucumber, tomatoes, parsley and mint.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(711,9,1,82,'Add salt and pepper, to taste.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(713,0,1,83,'In a large pan add 1 Tbsp. olive oil, onions, red pepper, tomatoes, mushrooms and zucchini and sauté all the vegetables until the onion has become translucent and the mushroom is cooked, about 5 minutes. Set vegetable mixture aside.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(714,1,1,83,'Take a large bowl and mix the other ingredients, 3 Tbsp. flour, 1/2 Tbsp. baking powder, 6 eggs, 3/4 cup milk, salt, pepper, 1/2 cup cheese (or not, depending on your dietary preference). Once you have this mix is time to add the vegetables and combine until everything is mixed perfectly.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(715,2,0,83,'Preheat oven to 320 degrees F.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(716,3,0,83,'Take your pastry dough and spread it out in a round baking dish, ensuring it is covering all surfaces. Pour in the vegetable mixture and put it in the oven for 40 to 45 minutes. It is ready when a toothpick comes out clean from the center.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(717,4,0,83,'Let cool for about 5 minutes and you are ready to serve! Enjoy.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(727,0,1,85,'Assist with peeling the potatoes and placing them into the pot. ');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(728,1,1,85,'Use a measuring cup to help pour enough water into the pot to cover the potatoes.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(729,2,0,85,'Preheat the oven to 350 F. Place the potatoes in the pot, cover with water, and bring to a boil. Cook for about 20 minutes, or until the potatoes are easily pierced with the knife. Remove from the heat, drain, and let sit until they are cool enough to handle. Cut the potatoes into small dice and set aside.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(730,3,1,85,'When potatoes have cooled and have been cut by the Big Chef, help place potatoes in a bowl to be set aside.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(731,4,0,85,'In a sauté pan, cook the bacon over medium to medium-low heat until browned. Remove the bacon from the pan, placing on a plate lined with paper towels.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(732,5,1,85,'Put paper towels on a plate to use with the cooked bacon.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(733,6,0,85,'Drain most of the bacon grease from the pan, leaving 1 tablespoon of fat in the skillet and add the onion. Cook over medium heat for about 5 minutes, until golden brown. ');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(734,7,0,85,'Add the bacon and wine to the onions and cook for another 5 minutes, stirring occasionally. ');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(735,8,0,85,'Add the potatoes and season with salt and pepper, to taste.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(736,9,1,85,'Help to season the potatoes with salt and pepper.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(737,10,0,85,'Remove the potato mixture from the heat and place half of it in an ovenproof dish. Have the little chef spread half the cheese slices atop the potato mixture. Cover this with the other half of the potato mixture. Top with the remainder of the cheese. ');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(738,11,0,85,'Bake in the hot oven for 20 minutes, or until golden brown and bubbling.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(739,0,1,86,'Measure all the ingredients.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(740,1,1,86,'In a bowl, mix together the flour,almond milk, salt, and vanilla extract.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(741,2,1,86,'Whisk in 1 egg yolk and 1 whole egg, one at a time');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(742,3,1,86,'Whisk the batter so that it runs smooth.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(743,4,1,86,'Give the bowl to big chef.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(744,5,0,86,'Melt butter in a medium saucepan over medium-low heat.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(746,7,1,86,'Cut bananas into circular pieces for topping the crepes.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(747,8,1,86,'Whisk the whipping cream in a bowl to make peaks.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(748,9,0,86,'While Little Chef cuts the bananas and whisks the cream, pour 2 tbsp of batter into the pan and rotate the pan so the batter coats it evenly. Cook the crepe for about 30 seconds on one side and then flip it to cook the other side. Serve hot.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(749,0,1,87,'Wash the onions, broccolini, and carrots.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(750,1,1,87,'Roughly chop the vegetables (depending on the Little Chef’s age).');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(751,2,1,87,'In a bowl, separate the egg yolks from the whites.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(752,3,1,87,'Measure out the broth, spices, feta, cherries, and pecans.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(753,4,0,87,'Preheat the oven to 350 degrees. While the oven is preheating, chop up all of your vegetables, pecans, and dried cherries and set aside for later.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(754,5,0,87,'On medium low heat, melt 1 oz. of butter and 1 tsp. of olive oil. Once melted, add 1 oz. of tapioca flour, stirring constantly. You are making a roux for your soup! Keep stirring for about 5-7 minutes until a somewhat khaki color is achieved.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(755,6,0,87,'To the roux, slowly add 2.5 cups of broth while stirring constantly. Once added, add 1/2 tsp. coarse black pepper, 1 tsp. of turmeric, 1/2 tsp. of allspice or ground cloves, 2 tsp. of garlic powder, 1 cup of broccolini STEMS (NOT florets), 1 cup of chopped carrots, and the 2 chopped red onions.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(756,7,0,87,'Let this simmer for 20 minutes. While the soup is simmering, in a baking sheet drizzle some olive oil and toss 1 cup of chopped broccolini florets, 1/4 cup of dried chopped cherries, 1/4 of chopped pecans, 2 oz. of crumbled feta, 1/4 tsp. of all spice, 1/4 tsp. sea salt, and 1/4 tsp. black pepper. INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(757,8,0,87,'Bake this mixture in the oven at 350 degrees for 5-7 minutes, depending on your oven.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(758,9,0,87,'Remove the soup from the heat and pour the pot’s contents into a high-powered blender and add the 3 egg yolks. Blend the soup until a uniform appearance has been achieved. Add salt to the soup to taste.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(759,10,0,87,'Ladle some of the soup into a bowl, top it with the broccolini floret crumble mixture, and enjoy!');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(760,0,1,88,'Measure out the ingredients.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(761,1,0,88,'Cut/peel the outside of the pineapple.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(762,2,1,88,'Mix wet ingredients together.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(763,3,1,88,'Mix dry ingredients together.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(764,4,1,88,'Cut pineapple into chunks.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(765,5,1,88,'Mix the wet and dry ingredients together.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(766,6,1,88,'Place the mixed ingredients into the baking dish.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(767,7,0,88,'Place the baking dish into the oven. ');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(768,8,0,88,'After 20 minutes pull out Clafoutti and let cool for 5 minutes.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(769,9,0,88,'Cut and serve.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(791,NULL,0,91,'Heat oven to 375°F ');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(792,NULL,0,91,'Set a large pot of salted water (approx. 5 liters) to boil.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(793,NULL,0,91,'Add penne and potatoes to the salted water.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(794,NULL,0,91,'Stir to make sure pasta doesn’t stick together.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(795,NULL,0,91,'Cook until tender, about 15 minutes. Drain penne and potatoes.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(796,NULL,0,91,'Heat butter/oil over medium-low heat in a frying pan.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(797,NULL,1,91,'Add onions to the pan and fry them until golden brown.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(798,NULL,1,91,'Combine milk/cream with salt and pepper.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(799,NULL,0,91,'In an ovenproof casserole dish, place 1/2 of the penne and potatoes. Let the Little Chef complete their duties before continuing.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(800,NULL,1,91,'Sprinkle with 1/2 of the grated cheese over the penne-potatoes.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(801,NULL,1,91,'Top with the remaining 1/2 of the penne-potatoes.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(802,NULL,1,91,'Pour the seasoned milk/cream evenly over the top.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(803,NULL,1,91,'Spread the browned onions on top.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(828,0,0,94,'Preheat oven to 425°F.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(829,1,1,94,'Toss your diced bread in two tablespoons olive oil, 2 cloves minced garlic, 1 teaspoon salt and 1/2 teaspoon pepper. ');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(830,2,0,94,'After chef koochooloo does step 1 bake for 10 minutes until golden brown.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(831,3,1,94,'Whisk together remaining 1/3 cup olive oil, 2 tablespoons balsamic vinegar, lemon juice, paprika, and 1 teaspoon salt.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(832,4,1,94,'Cool bread and toss with tomatoes, cucumbers, red onion, pine nuts and cilantro. Then combine with dressing. ');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(833,0,1,95,'Open chickpea cans, drain liquid to a bowl and keep for later.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(834,1,0,95,'Place chickpeas and garlic in workbowl of a food processor and pulse until chickpeas are roughly chopped.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(835,2,1,95,'Add in tahini, lemon juice, olive oil, and 1/4 cup of reserved liquid from cans of chickpeas.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(836,3,0,95,'Puree until smooth, stopping to scrap down sides of workbowl as needed. Thin hummus with additional reserved liquid if necessary. Add salt and pulse to combine.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(837,0,0,96,'Grind the poppy seeds in a coffee grinder in batches for about 15-20 seconds per batch, till they are ground soft and powdery.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(838,1,0,96,' Melt the butter in a saucepan over medium heat. Whisk in the milk, sugar, honey and salt. Bring to a simmer, stirring occasionally, till the sugar dissolves and the honey melts.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(839,2,1,96,'Beat the eggs using a whisker.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(840,3,0,96,'Pour about 1 cup of the butter mixture into a cup.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(841,4,0,96,'Immediately but gradually being drizzling the mixture into the beaten eggs. Whisk briskly and constantly till all of the hot liquid is integrated into the eggs. Do not pour too quickly, or you will scramble your eggs. It should take about a minute to drizzle all of the liquid.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(842,5,0,96,'Slowly pour the egg and butter mixture back into the saucepan, whisking constantly.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(843,6,0,96,'Continue to whisk and cook for 3-5 more minutes over medium heat till the mixture thickens and turns light yellow. It is ready when it thickly coats the back of a spoon and remove from heat.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(844,7,1,96,'After the butter mixture is removed from heat whisk the ground poppy seeds into the buttery liquid and stir well to blend all ingredients.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(845,8,1,96,'Allow filling to cool to room temperature before using. ');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(895,0,1,90,'Sprinkle the flour over the meat.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(896,1,0,90,'Heat half of both the butter and oil in a frying pan.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(897,2,0,90,'As soon as the butter foams, add the meat and brown it very quickly on all sides and set aside on a plate.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(898,3,1,90,'Add the rest of the butter and oil to the pan and sauté the onion, garlic and sage for a few minutes.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(899,4,1,90,'Add the mushrooms.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(900,5,0,90,'Stir well and when the mushrooms start to soften, continue cooking for about 5 minutes longer.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(901,6,0,90,'Add the wine to deglaze the bottom of the pan, using a wooden spoon to scrape away any crust that has formed.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(902,7,0,90,'Add the veal and cook, stirring, until the wine has evaporated.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(903,8,0,90,'Turn the heat down to low and cook for another 10 minutes.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(904,9,1,90,'Season with salt and pepper.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(905,10,1,90,'Add the cream.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(909,0,0,92,'Preheat the oven to 375° F, and grease a 9 inch pie tin.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(910,1,1,92,'Roll out two-thirds of the pastry and use it to line the pie tin.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(911,2,0,92,'Bake the pie crust for 7-10 minutes.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(912,3,0,92,'Melt the butter in a frying pan then add the onion and bacon. Fry over a low heat for 3 minutes.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(913,4,1,92,'Add the chopped onion and the bacon to the butter in the frying pan.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(914,5,1,92,'Add the meat in the frying pan.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(915,6,0,92,'Fry the meat until browned.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(916,7,0,92,'Stir in the flour.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(917,8,1,92,'Stir the mix until smooth.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(918,9,0,92,'Prepare the beef boullion, and add the bouillon with the wine in the pot.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(919,10,1,92,'Add the salt, marjoram, and paprika to the mixture.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(920,11,0,92,'Simmer for 5 minutes, let cool, spread into crust, then roll remaining pastry dough.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(921,12,0,92,'Brush the edges with beaten egg.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(922,0,0,93,'Heat the rice milk to a very warm temperature and add the margarine.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(923,1,1,93,'Whisk together the dry ingredients, flour, yeast, sugar, and salt-in a large bowl.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(924,2,0,93,'Mix the rice milk and margarine mixture with the dry ingredients until it forms dough.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(925,3,1,93,'Remove the finished dough onto a flat surface and knead it for about 5 minutes.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(926,4,1,93,'Let the dough rise and double in size. Cover with a dish towel or a plastic wrap. Leave for about 30 minutes.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(927,5,0,93,'After 30 minutes of the dough rising, divide the dough into 6 pieces. Use a rolling pin to form oval shapes about 15cm/6 inches long.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(928,6,0,93,'Preheat the stove to 400 F.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(929,7,0,93,'Use a sharp knife to cut four slits into each piece.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(930,8,0,93,'Carefully stretch and place the rolls on a baking sheet. Let them rest for around 30 minutes.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(931,9,0,93,'Bake for 15-20 minutes, or until golden brown.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(932,10,1,93,'Brush the melted butter over the rolls with a pastry brush.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(933,0,0,98,'Heat oven to 375°F.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(934,1,0,98,'Set a large pot of salted water (approx. 5 liters) to boil.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(968,0,0,100,'Preheat oven to 375°F. ');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(969,1,0,100,'Cut the bread into 1-inch thick slices. Give to the Little Chef.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(970,2,1,100,'After the Big Chef has sliced the bread, put the pieces on a baking sheet. Drizzle the bread with olive oil.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(971,3,0,100,'After Little Chef has put olive oil on bread, cook in the oven for 10 minutes. Once bread is done, set aside to cool.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(972,4,1,100,'Wash basil and pat dry. Tear the basil leaves into small pieces. ');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(973,5,1,100,'Place tomatoes, basil, salt and pepper in a bowl and drizzle with olive oil.  Stir to combine all ingredients.  ');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(974,6,1,100,'After the bread has cooled slightly, rub the garlic on the toasted bread.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(975,7,1,100,'Put a spoonful of the tomato mixture on each piece of toasted bread.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(976,0,0,97,'In a large pot of boiling salted water, cook pasta until al dente, about 13-15 minutes.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(977,1,0,97,'Drain the pasta.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(978,2,1,97,'Cut tomatoes in half.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(979,3,1,97,'Cut olives in half.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(980,4,1,97,'Wash parsley and pat dry. Take parsley leaves off the stems.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(981,5,1,97,'Tear parsley leaves into small pieces.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(982,6,0,97,'Meanwhile, in a large skillet, heat olive oil and cook garlic for 30 seconds. Add cherry tomatoes, oregano, salt and pepper. Cook on low heat for 10 minutes.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(983,7,0,97,'Add penne, olives, parsley and parmesan to skillet and toss to combine.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(984,0,0,101,'Preheat the oven to 400ºF and line a large baking sheet with parchment paper.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(985,1,1,101,'Dice the eggplant into bite-sized cubes and toss them onto the baking sheet.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(986,2,1,101,'Drizzle the eggplant with olive oil and sprinkle with salt and fresh cracked black pepper. ');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(987,3,0,101,'Roast the eggplant for 20 to 25 minutes, rotating the baking sheet half-way through the roasting time.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(988,4,0,101,'Make the pasta according to package directions.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(989,5,0,101,'Make the arrabiata sauce by heating a large pan over medium heat.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(990,6,1,101,'Add to the saucepan 2 tablespoons olive oil, then add the minced garlic and cook for 3 minutes until it is golden. Add the crushed tomatoes and red pepper flakes.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(991,7,0,101,'Bring the sauce to a simmer and let it cook for 20 minutes.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(992,8,1,101,'Once the eggplant is done roasting, add it to the arrabiata sauce, tossing to coat.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(993,9,1,101,'Spoon the eggplant arrabiata sauce over a bowl of pasta and serve.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(994,0,0,102,'Heat oil in heavy large pot over medium heat. Add onions, pancetta, prosciutto, and garlic and sauté until mixture begins to brown, about 10 minutes.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1000,0,1,103,'In a large bowl, dissolve yeast in 1/2 cup warm water. Add the sugar, shortening, salt, egg, remaining water and 4 cups flour; beat until smooth. Stir in enough remaining flour to form a stiff dough.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1001,1,1,103,'Turn onto a floured surface; knead until smooth and elastic, about 6-8 minutes. Place in a greased bowl, turning once to grease the top. Cover and let rise in a warm place until doubled, about 1 hour.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1002,2,0,103,'Punch dough down. Turn onto a lightly floured surface; divide in half. Shape each portion into a loaf. Place seam side down on greased baking sheets.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1003,3,0,103,'With a sharp knife, make four shallow diagonal slashes across the top of each loaf. Cover and let rise until doubled, about 1 hour.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1004,4,0,103,'Preheat oven to 350°.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1005,5,0,103,'Bake for 37-42 minutes or until golden brown. Remove from pans to wire racks to cool.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1006,6,0,103,'Slice Italian bread in thick slices.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1007,7,1,103,'Butter bread slices after it has been cooled and cut.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1008,8,0,103,'Cook bread in pan until both sides are golden brown.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1009,0,1,104,'Put 1 oz of the dried red dulse seaweed and 1 oz. of dried green wakame seaweed in a large bowl and cover with cold water. Let soak 5 to 10 minutes, until softened. Drain in a colander, pat dry and place in a serving bowl.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1010,1,0,104,'Watch over little chef, help slice the vegetables.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1011,2,1,104,'Cut and slice 1 cucumber, 1 avocado, handful of baby tomatoes, and 4 red radishes.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1012,3,1,104,'To make the dressing, whisk together 2 tbsp. vinegar, 2 tsp. sugar, 2 tsp. soy sauce and 1 tbsp. oil in a small bowl.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1013,4,1,104,'Spoon half the dressing over the seaweed, add the juice of 1 lime and toss gently. Taste and add salt to taste if necessary.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1014,5,1,104,'Mix the salad with the sliced radishes, tomatoes, cucumber and avocado. Season them lightly with salt and drizzle with the remaining dressing.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1015,0,0,105,'Heat the oven to 400°F.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1016,1,1,105,'Finely chop the onion, and mince the garlic clove.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1017,2,0,105,'Heat 1 tbsp. of vegetable oil in a large skillet over medium heat. Add the onion and garlic and cook them for 3 minutes, stirring often.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1018,3,0,105,'Add .75 lbs of beef and use a wooden spoon or a spatula to break it up while it cooks, until it is no longer red, about 3 minutes. Stir in 1/2 cup of the salsa, 2 tsp. of chili powder, and the 1/2 tsp. salt and 1/4 tsp. of pepper.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1019,4,1,105,'Top each tortilla with 1/4 cup of the beef mixture, spreading it to an inch from the edges. Sprinkle cheese evenly over the beef.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1020,5,1,105,'Roll up the tortillas and place them on a foil-lined baking sheet with the seam sides down. ');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1021,6,0,105,'Brush the taquitos lightly with vegetable oil, then bake them until the filling is heated through and the tortillas are lightly browned, about 8 to 12 minutes.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1023,0,1,106,'In a bowl combine 1 tsp. of chile powder, 1 tsp. of salt, 1/2 tsp. cumin, 1/2 tsp. onion powder, 1/4 tsp. of garlic powder, 1 tbsp. of cornstarch, 2 tbsp. of lime juice, 1/4 cup of water, and 2 tbsp. of olive oil. After the marinade is complete pour half into one large ziplock bag with .75 lbs of chicken then pour the other half in another ziplock bag with .75 lbs. of flank steak.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1024,1,0,106,'Marinate for at least 2 hours or overnight');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1025,2,0,106,'Grill meat until cooked through, slice  into strips.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1026,3,0,106,'Heat the remaining 2 tablespoon of oil in a large nonstick skillet until shimmering sautee the chopped onion over medium heat. Cook until soft.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1027,4,0,106,'Add the sliced green and red bell pepper and continue to cook until peppers are soft and onions are beginning to brown and crisp.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1028,5,1,106,'Separate the cooked vegetables into two bowls and mix chicken in one and beef in the other. ');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1030,1,1,107,'Cut the 5 avocados, dice 1/4 of a small red onion, and mince 1 garlic clove.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1031,2,1,107,'In a large bowl, mash together the avocados, onion, garlic, 2 tbsp. of olive oil, juice from 3 limes, and salt and pepper – to taste.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1032,3,0,107,'Cut the pomegranate in half, then tear the halves into smaller pieces.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1033,4,1,107,'Pick out all the seeds and discard the flesh from the pomegranate.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1034,5,1,107,'Fold a small handful of pomegranate seeds and a small handful of cilantro leaves, roughly chopped into the guacamole.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1035,6,0,107,'Give everything a good mix. ');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1036,0,0,108,'Cut the 4 avocados in half and take the seeds out.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1037,1,1,108,'Cut the avocados, 2 tomatoes, 1 purple onion, and 2 cloves of garlic.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1038,2,1,108,'In a large bowl, mash the 4 cut up avocados.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1039,3,1,108,'Add the cut onion garlic and tomatoes, 1 bunch of cilantro, 2 chile  peppers (optional),  juice from 1 lime, and salt and pepper – to taste.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1040,4,0,108,'Give everything a good mix. ');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1041,0,0,109,'Dice the red bell pepper.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1042,1,0,109,'Open the 2 cans of tuna.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1044,3,1,109,'Add 1 cup of cream cheese, diced bell pepper, 2 cans of tuna, small bag of thawed vegetables, and 1 tbsp. of mayonnaise to a large bowl and stir to mix. Give the bowl to the Big Chef.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1045,4,0,109,'Put all ingredients in a blender and mix until well combined.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1046,5,0,109,'Remove the crusts of the bread and cut into shapes, if desired.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1047,6,0,109,'Lay out 6 slices of bread. Take the blended ingredients and spoon an equal amount on to each piece of bread. Put one slice of cheese on each sandwich and top with another piece of bread. The sandwiches are ready to serve.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1048,0,0,99,'Chop onion in fine slices.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1049,1,0,99,'Heat butter in pan until melted.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1050,2,1,99,'Add onion to pan with melted butter.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1051,3,1,99,'Chop and press garlic cloves and add to pan.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1052,4,0,99,'Add risotto rice to the pan, cook gently stirring all the time until rice becomes translucent.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1053,5,0,99,'Boil tomatoes for 5 minutes or until skin starts to break. Take tomatoes out of boiling water, wait for them to cool off and remove skin.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1054,6,0,99,'Boil 800 ml of water and add Maggi Vegetable Broullion stock. Dissolve and add to the risotto. Simmer for about 25 minutes until rice is ready.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1056,7,0,99,'Fry bacon slowly in a pan until crispy. Remove from heat and let little chef dry with paper.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1057,8,1,99,'Dry bacon with paper.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1062,0,0,110,'Cook & shred the chicken beforehand, or buy pre-cooked, shredded chicken.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1063,1,0,110,'Cut cucumbers, tomatoes, and cheese into thin slices.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1064,2,0,110,'Chop up the celery into small pieces.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1065,3,1,110,'Lay out 12 slices of bread and spread butter thinly on one side of each slice.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1066,4,1,110,'Put 2 layers of cucumber slices on top of half of the bread.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1067,5,1,110,'Sprinkle the cucumber slices with salt.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1068,6,1,110,'Put a plain slice on top of each slice with cucumber.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1069,7,1,110,'Lay out 12 slices of bread and spread mayo thinly on one side of each slice.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1070,8,1,110,'Put the chicken, mayo, onion, celery and salt in a bowl and mix well.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1071,9,1,110,'Put ¼ cup of chicken salad on top of half of the bread slices.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1072,10,1,110,'Put a plain slice on top of each slice with chicken salad.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1073,11,1,110,'Lay out 12 slices of bread and spread butter thinly on one side of each slice.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1074,12,1,110,'Put a layer of cheese on top of half of those slices.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1075,13,1,110,'Add a layer of tomato slices on top of the cheese.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1076,14,1,110,'Put a plain slice on top of each slice with cheese & tomato.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1077,15,0,110,'After the sandwiches are assembled by the Little Chef, cut the crusts off and slice each sandwich into 4 triangles.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1078,0,0,111,'Hull the strawberries, removing the leaves and stems.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1079,1,1,111,'Place the strawberries in a colander and rinse well.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1080,2,1,111,'Place the strawberries in a dessert bowl or cup.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1081,3,1,111,'Pour in cream to your liking.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1082,0,0,112,'Get yourself a wide, casserole-type pan and fill it with boiling water from the kettle. Bring it to a light simmer over a medium heat.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1083,1,0,112,'Depending on your pan, a really soft poached egg should take around 2 minutes and a soft to firm one will need 4 minutes.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1084,2,1,112,'Crack one of your eggs into a cup and gently pour it into the water in one fluid movement. ');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1085,3,1,112,'Repeat with the rest of the eggs. ');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1086,0,1,113,'Finely chop the kumquats and oranges and combine. ');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1087,1,1,113,'Using a mug, measure them into a large pot. ');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1088,2,1,113,'Add one mugful of sugar to the pot for every mugful of the fruit mixture. Mix in the lemon juice, about 4 tablespoons.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1089,3,1,113,'Using a coffee mug, add 3 mug full of water per each mugful of fruit. Give to big chef.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1090,4,0,113,'After chef koochooloo has prepared orange and kumquat mixture, let stand in a cool place for 12 hours, or overnight.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1091,5,0,113,'Bring the fruit mixture to the boil, reduce heat and simmer until the rind is very tender. Remove from heat, and measure cooked fruit.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1094,0,0,114,'Put the nuts into a food processor ahead of time until it ends up in a chunky powder.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1095,1,0,114,'Zest the lemon.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1096,2,1,114,'Add cinnamon and lemon zest into the nut powder.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1097,3,1,114,'Mix the butter, honey, and vanilla into a separate bowl.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1098,4,1,114,'Combine the dry ingredients with the wet ingredients.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1099,5,1,114,'Roll little balls out of the dough that you made.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1106,0,1,116,'Measure all the ingredients.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1107,1,0,116,'Chop the onion and cut the clove of garlic in half.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1108,2,0,116,'Heat a large dutch oven or pot with a drizzle of the olive oil. Add the onion, garlic and a cup of broth. Cook until the onion has softened. Remove the garlic.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1109,3,0,116,'Chop the carrots, red bell peppers and artichoke hearts. Slice the mushrooms. Give all to the Little Chef. Let the Little Chef complete their duties before continuing.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1110,4,1,116,'Add the carrots, bell peppers and snow peas to the pan with the onions. Cook until softened');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1111,5,0,116,'Stir a little, then salt to taste and add the arborio rice');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1112,6,1,116,'Add artichoke hearts and mushrooms, continue to cook and add broth as needed.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1113,7,1,116,'Add the lima beans, more broth and cook for 10 minutes.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1114,8,1,116,'    Add the tomato puree, any remaining broth, and the saffron and paprika.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1115,9,1,116,'Stir one last time. (Note: It is important not to over stir.) Cook on high for 7 minutes. Reduce temperature to medium and cook for 10 more minutes.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1117,1,0,117,'Grill the eggplant and peppers and tomatoes for 4-5 minutes or until needed, the tomatoes may need less time and can be pulled off the grill sooner.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1118,2,1,117,'Remove the skins and seeds of the vegetables once they have cooled. Cut up the grilled vegetables.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1119,3,0,117,'Crush the garlic.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1121,5,1,117,'Mix the vegetables, garlic, and parsley in a bowl.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1122,6,0,117,'Mix vinegar, olive oil, and salt into a small bowl.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1123,7,1,117,'Add the bowl of seasoning to the chopped vegetables.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1124,0,1,118,'Place the pomegranate juice in a medium-size pot. Add 2 Tbsp water into the pot along with the apple, sugar, pomegranate seeds and cinnamon stick.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1125,1,0,118,'Cook for about 10 minutes at medium-low heat. Then turn the heat down to low and cook for 20 minutes at low heat.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1126,2,0,118,'Add the lemon juice into the marmalade a few minutes before you turn off the heat and stir. Wait until it cools down a little bit, then place it into a clean jar and close the lid tightly.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1127,0,1,119,'Chop and dice onions, tomatoes, and peppers.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1128,1,0,119,'Heat the butter in a pot, sauté the onions for a couple of minutes.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1129,2,0,119,'Add the meat, once all sides are brown, add in peppers.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1130,3,0,119,'Stir for a few minutes.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1131,4,0,119,'Add tomato paste and stir for another couple of minutes.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1132,5,0,119,'Add tomatoes and cook for another 5 minutes.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1133,6,0,119,'Add the hot water and let it simmer until the meat is tender, it will take around an hour. ');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1134,7,0,119,'Wash and prick the eggplants with a fork on at least two sides.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1135,8,0,119,'Place the eggplants on a gas burner or under a broiler, turning them frequently until the eggplant is collapsed and the skin is charred. ');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1136,9,0,119,'Let them cool, then peel the eggplants and discard the stems. ');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1137,10,1,119,'Mash the cooked eggplants together with the back of a fork, in a bowl, and then mix with the lemon juice. ');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1138,11,0,119,'Heat the butter in a pot. Add flour and stir constantly to make a roux on low heat.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1139,12,0,119,'Warm the milk and add slowly. Whisk to make the mixture smooth. Simmer for 4-5 minutes. ');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1140,13,0,119,'Add the eggplant puree and mix well.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1141,14,0,119,'Add some salt, black pepper, and cheese. Mix well. Simmer for another 2-3 minutes. ');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1142,0,0,120,'Cut the crust off of the 4 slices of bread.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1143,1,1,120,'Flatten the slices of bread with a plate or a rolling pin. Do not flatten one edge so we can combine the bread slices.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1144,2,1,120,'Combine the bread with a rolling pin or by using your fingers.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1145,3,1,120,'Place the slices of ham and cheese onto the bread.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1146,4,1,120,'Place the spinach on top of the ham and cheese.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1147,5,1,120,'Roll the sandwich lengthwise( the longer way) and make sure to pinch closed.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1148,6,0,120,'After chef koochooloo has done the tasks place the sandwich into the fridge for 1 hour.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1149,7,0,120,'Take out and cut into pieces.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1150,0,1,121,'Cut the onion in half and add to the water, along with the bay leaves. ');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1151,1,0,121,'Fill a large soup pot with water, enough to completely submerge the octopus.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1152,2,0,121,'Add the onion.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1153,3,0,121,'Bring the water to a boil, and submerge the octopus 3 or 4 times to scald it. ');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1154,4,0,121,'Bring the water back to a boil and place the octopus in the water, leave it in there.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1155,5,0,121,'Reduce the heat to a simmer, and cook for about 20 minutes, larger ones may take up to 45 minutes.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1156,6,0,121,'Remove the octopus from the water.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1157,7,0,121,'Cut the cooked octopus into ovals.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1158,8,0,121,'Slices should be ½” thick.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1159,0,0,122,'Watch over the little chef. ');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1160,1,1,122,'Place the bread slices in a medium bowl topped with a fine mesh sieve. ');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1161,2,1,122,'Squeeze the tomato halves over the sieve to remove the seeds and allow the juices to run into the bowl with the bread.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1162,3,1,122,'Place the tomatoes into the pitcher of your blender as you work.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1163,4,1,122,'Press the seeds and any pulp through the sieve using a rubber spatula or your hands.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1164,5,1,122,'Allow the bread to soak in the tomato juice, will take 20 to 30 minutes.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1165,6,0,122,'Use the blender and process the tomatoes, the bread and the rest of the ingredients on the highest speed until it is smooth. ');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1166,7,0,122,'Once the blender is done, use a whisk to bring all of the ingredients together. ');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1167,8,0,122,'Cover with plastic wrap and refrigerate for 2 to 3 hours. ');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1169,0,1,123,'In a bowl, beat the 8 egg yolks with the sugar until they are blended.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1170,1,1,123,'Add the corn flour to yolks and beat for 2 minutes.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1171,2,0,123,'Cook the 2 cups of milk over low heat with the lemon zest and the cinnamon stick.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1172,3,0,123,'Once milk is hot remove the cinnamon stick.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1173,4,0,123,'Gradually add the warm milk to the mixture of yolks and sugar without stopping to beat.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1174,5,0,123,'Continue to mix until the mixture thickens.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1175,6,0,123,'Let it cool, put in the fridge for 3 hours. ');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1176,7,0,123,'Sprinkle a tablespoon of brown sugar on the top of the cream and burn it with the blowtorch to make the caramelized layer.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1190,0,0,23,'Bring the 2lb potatoes, 1 cup of broth, 1 cup water, 1 teaspoon salt, sugar, and 1 tablespoon vinegar to boil in 12-inch heavy-bottomed skillet over high heat. Reduce heat to medium-low, cover, and cook until potatoes offer no resistance when pierced with paring knife, 15-18 minutes. Remove cover, increase heat to high (so cooking liquid will reduce), and cook 2 minutes.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1191,1,0,23,'Drain potatoes in colander set over large bowl, reserving cooking liquid. Set drained potatoes aside. Pour off and discard all but ½ cup cooking liquid (if ½ cup liquid does not remain, add water to make ½ cup). Whisk remaining tablespoon vinegar, mustard, and oil into cooking liquid.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1193,2,1,23,'Peel the boiled potatoes.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1194,3,1,23,'Add ½ cup cooked potatoes to bowl with cooking liquid mixture and mash with potato masher or fork until thick sauce forms (mixture will be slightly chunky).');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1195,4,1,23,'Add remaining potatoes, onion, cornichons, and chives, folding gently to combine.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1196,5,1,23,'Season to taste with salt and black pepper.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1197,0,0,24,'Beat 4 egg whites to soft peaks, adding a pinch of salt towards the end.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1201,1,1,24,'Beat egg yolks and sugar until light yellow. Add 500ml milk and 1 cup of flour, a little at a time to avoid lumps.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1202,2,1,24,'Fold in egg whites. Let the batter rest for about ten minutes.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1206,0,1,26,'Chop up the 2 small cabbage, 1 cup of onion, and garlic clove.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1215,0,0,29,'Set out 4 shallow pans (pan 1 for flour, pan 2 for mixture, pan 3 for breadcrumbs, and pan 4 for parchment paper).');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1216,1,0,29,'Line pan 4 with parchment paper (alternatively, you can just use a cutting board instead of pan 4).');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1217,2,0,29,'Fill pan 2 with 2 eggs, 1 tbsp cream and lemon juice (squeeze 2 lemon wedges).');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1220,3,1,29,'Fill pan 1 with 1.2 cups flour and fill pan 3 with 1 cup breadcrumbs.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1222,4,0,29,'Heat oil in a large skillet at medium heat until it reaches 350- 360° F.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1223,5,1,29,'Season the 6 cutlets on both sides with salt and pepper as desired.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1224,6,1,29,'Use tongs to dredge the cutlet in the flour of pan 1,being sure to evenly coat both sides.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1225,7,1,29,'Use tongs to coat the cutlet in the mixture of pan 2 and use tongs to coat the cutlet in the bread crumbs of pan 3.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1226,8,1,29,'Press down the bread crumbs into the cutlet on both sides.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1227,9,1,29,'Move cutlets to a cutting board or to a pan lined with parchment paper (pan 4) to sit for 15 minutes while they dry.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1228,0,0,124,'1. Cut the potatoes in half and place them in a large pot and cover with cold water. Add some salt, and cook until a easily pierced with a fork.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1229,1,0,124,'2. Once the potatoes are finished boiling, place them in a bowl full of ice.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1230,2,0,124,'3. Grate onion into a large bowl and finely chop the parsley.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1231,3,1,124,'4. Peel the potatoes and mash them.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1232,4,1,124,'5.  Add the meat, bread crumbs, eggs, parsley, spices, salt, and pepper to the bowl.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1233,5,1,124,'6. Mix with hands. (You might want to wear gloves, this gets messy!)');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1234,6,1,124,'7. Scoop individually with your hands and roll each into a ball, then flatten. (Iranians usually make oval shapes, but you can have fun with various geometric shapes. Experiment with making squares, triangles, pentagons, etc.) Give to Big Chef to cook.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1235,7,0,124,'8. After the Little Chef has performed their duties, heat olive oil in a pan on low heat. Place the patties in the pan with some distance between each. (Tip: Take a very small piece of the one of the patties and place in the pan, fry it a bit, then taste it to assess whether you need any more spices.)');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1236,8,0,124,'9.  Once all the kotlets are placed in the pan, fry on low heat. Stay near! Flip them when the kotlets seem crispy and have darkened a bit.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1237,0,1,125,'Mix all the meatball ingredients together and roll into balls, 2-1/2 inches in diameter, firmly packed.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1238,1,0,125,'Refrigerate balls for 1 hour.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1239,2,0,125,'To make the broth, heat 3 tbsp oil in a pan or soup kettle, add 2 sliced onions and 1/4 tsp turmeric, and stir-fry over moderate heat until light brown, about 3 minutes.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1240,3,1,125,'Add 1 cup chopped tomatoes and 1 tsp salt to broth mixture and stir-fry for 2 minutes more.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1241,4,0,125,'Add 5 cups water and bring to a boil.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1242,5,1,125,'Take each herbed ball and roll it firmly; add the balls to the broth very carefully, one at a time, so that they do not not fall apart');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1243,6,0,125,'Cover the pan and cook over moderately low heat, without stirring, for 45 minutes.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1244,7,0,125,'Add 1 cup green peas and cook for 10 minutes more.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1245,8,0,125,'The broth will reduce somewhat, the balls swell considerably due to the expansion of the rice.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1246,0,1,126,'Measure and wash 3 cups rice in cold water, make sure that you have it a bowl with enough room to cover it with water and repeat this 2 or 3 times, then add some salt to it, and leave it to soak for 2 hours.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1247,1,1,126,'Pit 5 cups sour cherries.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1248,2,0,126,'Roll the ground turkey into small circular balls.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1249,3,0,126,'Take the sour cherries and place them in a saucepan, add 1/4 cup sugar, stir and simmer for about 15 minutes at medium heat. After this take the cherries and with a colander separate them from the juice. Return the drained juices to the pot and let them boil and medium to low heat for about 10 minutes more. (We will use this juice later).');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1250,4,0,126,'Fry the turkey balls in a saucepan lightly oiled, add 2 tsp turmeric, 2 tsp cinnamon, cardamom, cloves, 1 tsp rose water, 1 tsp cumin, ginger and stir. After you have this mix turn off the heat and add the cherries. Be sure to mix properly in order to coat the cherries with the seasoning mix.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1251,5,0,126,'Let the rice boil on medium heat for about 8-10 minutes, until soft (but not cooked all the way) and drain the rice. In the same pot, add 1 tbsp saffron, grape-seed oil and enough water to cover the surface of the pot.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1252,6,0,126,'Then add the first layer of basmati rice.Take a few spoons of your cherry/turkey mixture and spread on top of the rice while mixing gently. Repeat this process with the remainder of the rice and your cherries. Cook on low, very low heat for another 50 minutes to an hour.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1253,7,0,126,'Now we are almost at the end. To add the finishing touches, heat 2 table spoons of oil in a small sauce pan and add the reserved cherry syrup you made before and add 1 teaspoon of cumin. Pour this over the rice as you serve it.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1254,0,1,127,'Break 3 eggs into bowls');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1255,1,1,127,'Beat the eggs until stiff');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1256,2,1,127,'Chop 2 green onions in small pieces');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1257,3,1,127,'Add a pinch of salt');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1258,4,1,127,'Add 1/4 cup milk and whisk until well mixed');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1259,5,0,127,'Add vegetable oil to a frying pan and transfer the mixture into the pan.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1260,6,0,127,'Fry the mixture over medium heat, flip when hardened.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1261,0,1,128,'Rinse and soak 1 1/2 cups barley in cool water for an hour.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1262,1,1,128,'Arrange the chopped onions at the bottom of the stockpot, add the chicken, 2 stalks celery, 1 garlic, bay leaf, 1/4 turmeric, salt and pepper. Add enough water to cover all of the ingredients.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1263,2,0,128,'After all ingredients in pot bring to a boil on medium-high. Reduce the heat, cover and cook on medium-low heat for about 45-50 minutes or until the chicken is fully cooked.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1264,3,0,128,'Remove the pot from the heat and let cool. Drain the soup and pour into a bowl for later use. Separate the chicken from the bones and shred into small pieces.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1265,4,0,128,'Place the soaked and drained barley in a large pot, add the strained chicken soup and enough water to cover up to 3 inches.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1266,5,0,128,'Bring to a boil for a couple of minutes on medium-high heat, reduce the heat, cover leaving the lid a little ajar and cook for 45 minutes.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1267,6,0,128,'To the pot add the shredded chicken and the shredded carrots. Add more hot water if needed. Cover and cook for another 40- 45 minutes on medium-low heat.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1268,7,0,128,'In the last 15 minutes of cooking add 3 tbsp lemon juice, taste and adjust the seasoning.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1269,0,1,89,'Add water to the oats and let them soften for 20 minutes.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1270,1,0,89,'Wash and cut apples in small pieces.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1271,2,0,89,'Grate the apples.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1272,3,1,89,'Juice the lemons into a small bowl.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1273,4,1,89,'Add the sweetened condensed milk to the oats and stir.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1274,5,1,89,'Sprinkle the lemon juice over the apples.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1275,6,1,89,'Add the grated apples to the oat mixture.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1276,11,0,90,'Let it bubble for 2 minutes, then remove from the heat and serve.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1277,13,0,92,'Bake for about 40 minutes until golden brown.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1278,9,1,99,'Cut bacon in small pieces');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1279,10,0,99,'Add green beans to pan and stir-fry until cooked.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1280,11,0,99,'Finally add green beans and bacon to Risotto.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1281,12,1,99,'Stir risotto gently while mixing with rest of the ingredients until ready to serve.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1282,2,0,98,'Add penne and potatoes to the salted water. tir to make sure pasta doesn’t stick together.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1283,3,0,98,'Cook until tender, about 15 minutes. Drain penne and potatoes.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1284,4,0,98,'Heat butter/oil over medium-low heat in a frying pan.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1285,5,0,98,'Add onions to the pan and fry them until golden brown and put onto a plate.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1286,6,1,98,'Combine nestle creamer with salt and pepper.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1287,7,0,98,'In an ovenproof casserole dish, place 1/2 of the penne and potatoes.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1288,8,1,98,'Sprinkle with 1/2 of the grated cheese over the penne-potatoes.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1289,9,1,98,'Top with the remaining 1/2 of the penne-potatoes.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1290,10,1,98,'Pour the seasoned milk/cream evenly over the top.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1291,11,1,98,'Spread the browned onions on top.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1292,12,0,98,'Bake covered for 10-15 minutes until steaming hot and cheese is melted.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1293,6,0,23,'Serve warm or at room temperature; it tastes best after resting at room temperature for an hour or so to let the flavors mingle and the potatoes soak in more of the dressing.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1294,3,0,24,'Warm up a large frying pan (10 - 12 inches in diameter) and melt 1 to 2 tablespoons of butter in it.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1295,4,0,24,'Stir the batter again, gently, and pour into hot pan. Sprinkle the raisins evenly over the top. Flip over (you may have to cut it into several portions to flip).');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1296,5,0,24,'Cut or pull the pancake apart into bite size pieces while it continues to cook. When it is browned a bit, it is ready to serve.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1297,1,0,26,'In a 12-inch skillet over medium heat, heat oil and add the chopped vegetables.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1298,2,0,26,'Cook 8 minutes or until tender. Add the 1lb of sausage and cook about 4 minutes until sausages are heated through and vegetables are tender.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1299,3,1,26,'Stir in 1 tbsp of mustard, 1 tbsp of vinegar, some salt and pepper. Cook 1 minute longer.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1300,0,1,28,'Chop up the cabbage and onion into small pieces and add salt.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1301,1,0,28,'Heat the oil in a skillet and caramelize the sugar a bit (this takes about 2-3 minutes on medium heat). Sugar will melt and turn brownish color. Do not burn.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1302,2,0,28,'Add the chopped cabbage, onions and the 1.2 tsp of caraway seeds into the skillet. Cover, and cook for about 30 minutes on low, stirring regularly, until light brown in color. Season with pepper.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1303,3,1,28,'Dice the 2 cooked sausage made by the big chef beforehand once it is cooled.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1304,4,0,28,'Boil some water and cook 5 oz. of noodles until soft, drain. Let stand for a few minutes. Season with more salt and pepper to taste.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1305,5,1,28,'Combine the cabbage mixture, cooked sausage, and boiled noodles made in the big chef step 5 to finish the dish.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1306,10,0,29,'Add cutlets to the oil,fry until golden on each side(about 1-2 minutes).');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1307,0,1,22,'Place 300g of all-purpose flour in a bowl with 1.4 tsp salt and add the 200ml of lukewarm water, then add 30ml oil. Stir with a spoon until it comes together and you can work it with your hands.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1308,1,1,22,'Knead the dough until it is smooth, but not sticky, about 5 minutes. If you need to add more flour, only add it 1 teaspoon at a time.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1309,2,0,22,'Form the dough into a smooth ball, brush it with a little oil and place it back in the bowl for 1 hour, room temperature. Covering the dough with plastic wrap is fine, too.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1310,3,0,22,'Heat the 100g of butter in a pan until foaming and add the breadcrumbs. Toast them, stirring constantly, until they are medium brown. Let cool.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1311,4,0,22,'Soak the 1.5 cups of raisins in the 4 tsp orange juice. You can heat them for 30 seconds in the microwave and then soak them until you are ready for them.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1312,5,1,22,'Peel, core and chop the 7 cups of apples into small pieces. Add the 150g sugar, 4 tbsp lemon juice, 2tsp lemon zest, 1.5 cups raisins and 1.4 tsp ground cinnamon and mix well.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1313,6,0,22,'Preheat oven to 400° F. Roll out the dough on a lightly floured board to about 9 inches by 13 inches. Using your hands, gently stretch the dough thinner on all sides, working your way around the sheet of dough.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1314,7,0,22,'Take apple mixture and spread it over the center of the dough.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1315,8,0,22,'Brush exposed dough with melted butter. Fold in one side of the dough over the filling.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1316,9,1,22,'Spread the breadcrumbs over 2/3 of the dough and pat down evenly.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1317,10,0,22,'Fold other side of dough up and over filling to form a roll. Brush with butter. Bake at 400°F for 20 minutes and then at 350°F for 40-60 minutes longer. Remove from oven.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1318,11,0,22,'Transfer to a serving platter with a large spatula (or two). Cut into 1½-inch wide slices.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1319,NULL,0,129,'Cut the potatoes in half and place them in a large pot and cover with cold water. Add some salt, and cook until a easily pierced with a fork.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1320,NULL,0,129,'Once the potatoes are finished boiling, place them in a bowl full of ice.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1321,2,0,129,'Grate onion into a large bowl and finely chop the parsley.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1322,NULL,1,129,'Peel the potatoes and mash them.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1323,4,1,129,'Add the meat, 1/2 bread crumbs, 2 eggs, 3 tbsp parsley and spices, salt, pepper to the bowl.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1324,NULL,1,129,'Mix with hands. (You might want to wear gloves, this gets messy!)');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1325,NULL,1,129,'Scoop individually with your hands and roll each into a ball, then flatten. (Iranians usually make oval shapes, but you can have fun with various geometric shapes. Experiment with making squares, triangles, pentagons, etc.) Give to Big Chef to cook.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1326,NULL,0,129,'After the Little Chef has performed their duties, heat olive oil in a pan on low heat. Place the patties in the pan with some distance between each. (Tip: Take a very small piece of the one of the patties and place in the pan, fry it a bit, then taste it to assess whether you need any more spices.)');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1327,NULL,0,129,'Once all the kotlets are placed in the pan, fry on low heat. Stay near! Flip them when the kotlets seem crispy and have darkened a bit.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1328,0,1,134,'Cut the leeks for the confit.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1329,1,1,134,'In a stand mixer, mix flour and salt and add butter in small pieces.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1330,2,1,134,'Add all ice water and cider vinegar in bowl.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1331,3,1,134,'Gently apply water-vinegar mixture to the flour so it forms a smooth mixture.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1332,0,1,138,'Chop the 1lb pork shoulder into small squares');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1333,1,1,138,'Chop 1 onion and 2 garlic cloves');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1334,2,0,138,'In a skillet, brown pork, then add 1 tsp salt, pepper(to your liking), onions, and garlic. Cook until pork is done.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1335,3,0,138,'Add 8 oz. tomato sauce, 1 tsp vinegar, 1 tsp soy sauce, 1 bay leaf, and water. If gravy is too thick, add more water.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1336,4,0,138,'Add 10 oz. peas and cook until done. Top with the 1 can of pimentos.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1337,5,0,138,'Serve over hot rice.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1338,0,1,140,'Grind walnuts (1/2 cup) and almonds (if bought whole).');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1339,1,1,140,'Add 1 cup fresh dates and some water to processor.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1340,2,0,140,'Mix/blend dates and water in processor till paste like.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1341,3,1,140,'Mix in 1 tsp. cinnamon and 1/2 tsp. cardamom.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1342,4,1,140,'Kneed in walnuts.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1343,5,1,140,'Form balls.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1344,6,1,140,'Spread honey and cover in almonds.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1345,0,1,139,'Chop green chilies, grate carrots, and finely chop the coriander');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1346,1,0,139,'In a pan (tawa) dry roast oats until slightly brown');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1347,2,0,139,'Mix oats to a powder.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1348,3,1,139,'In a pan, add oil, mustard seeds, urad dal, and channa dal.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1349,4,0,139,'Allow mustard to splutter and dals to turn golden.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1350,0,0,141,'Heat and stir 1 Tbsp. butter in a skillet.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1351,5,1,139,'Add chilies, coriander, and carrots.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1352,1,1,141,'Add the onion and stir until yellow.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1353,2,0,141,'Add 1 cup parsley and 1 Tbsp. flour. Stir till smooth.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1354,3,0,141,'Take off heat. Set aside.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1355,4,1,141,'Beat 5 eggs. Season with salt and pepper.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1356,5,1,141,'Pour onion mix onto eggs. Mix well.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1357,6,0,141,'Heat ½ Tbsp. butter.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1358,7,0,141,'Add egg mixture into skillet.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1359,8,0,141,'Cook until bottom of eggs are golden.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1360,9,0,141,'Flip eggs to cook other side.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1361,6,1,139,'Add turmeric powder');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1362,7,0,139,'Fry for a minute');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1363,8,1,139,'Add seasoning to powdered oats mixture. Add the curd to make a batter (you can add as much curd to the mixture to achieve consistency, but don\'t add water).');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1364,9,1,139,'Grease the idli steamer plates with oil.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1365,10,1,139,'Pour batter into each area of the steamer.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1366,11,0,139,'Steam idlis for 15 minutes');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1367,0,1,142,'Dice 2 large onions. Mince 4 garlic cloves. Chop tomatoes (if bought whole)');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1368,1,0,142,'Cook 1 cup lentil in about 4 cups of salted water.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1369,2,0,142,'Bring to boil. Reduce heat and cover. Simmer for 15-30 minutes. Until lentils are quite tender.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1370,3,1,142,'Add 1 cup of basmati rice to lentils.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1371,4,0,142,'Continue simmering until rice is cooked. Add water if necessary.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1372,5,1,142,'Add tomatoes, chili flakes, salt and pepper to taste.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1373,6,0,142,'Cook 1 cup macaroni in separate pot.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1374,7,0,142,'Rinse and strain when done.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1375,8,0,142,'Fry onions and garlic in oil till golden.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1376,9,0,142,'Let bubble for 10-20 minutes or until thickened sauce like.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1377,10,0,142,'(Optional) Blitz sauce in food processor until smooth.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1378,11,1,142,'Mix lentils, rice and macaroni in one pot.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1379,0,0,143,'Soak the basmati rice in water for 10 minutes. Turn the stove on for the Little Chef.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1380,1,1,143,'Heat 1 tbsp of ghee in a pan and put the rice into the pan. Let the Big Chef mix on the stove, and cook it until a nice aroma is produced.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1381,2,1,143,'Add bay leaf, cinnamon, cloves, cardamom, and 1/4 tsp cumin seeds to the pan, and let the Big Chef turn on the stove for you.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1382,3,0,143,'Add the green chilies, onions, and 1 tsp ginger-garlic paste and fry everything for 5 minutes until everything becomes tender.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1383,4,1,143,'Help the Big Chef with adding the ingredients into the rice cooker.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1384,5,0,143,'Put this mixture in a rice cooker along with salt, green peas, fried rice, 1 1/2 tsp coconut milk, and 2 cups water. Cook the rice according to the manufacturer’s directions.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1385,12,1,142,'Place some of the lentil mixture on each plate.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1386,0,0,144,'Preheat oven to 350 degrees F. Oil a baking sheet with cooking spray and set aside.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1387,1,0,144,'Melt 3 tbsp butter over stove or in the microwave.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1388,2,1,144,'In a large bowl stir together 2/3 cups of milk, 3/4 cup cheese, 1 egg, and melted butter.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1391,3,1,144,'Sprinkle in 1 3/4 cup tapioca flour and 1 cup flour and stir until it forms into dough.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1394,0,0,145,'Peel the potatoes (zemiaky) and shred them using a fine food grater. Fill a large pot with salted water and bring to a boil.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1395,1,0,145,'Drain as much water as you can from the potatoes. Add salt (soľ) and two cups of flour (múka). Mix well.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1397,2,0,145,'Place the dough on a wooden cutting board. Then using a knife, cut off small pieces of the dough and “toss” the dumplings (halušky) into boiling salted water. The smaller, the better.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1400,3,1,145,'Boil for few more minutes and scoop out with a large slotted spoon.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1401,4,0,144,'Knead dough for two minutes on a lightly floured surface.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1403,4,1,145,'Top with sheep cheese (bryndza) and bacon.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1404,5,1,144,'Roll the dough into golf ball-sized pieces, and place onto the prepared baking sheet.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1405,6,0,144,'Bake in preheated oven until golden brown for about 10 to 15 minutes.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1406,0,1,151,'Cut 1 cup potatoes, 1/2 cup carrots, and 1/2 cup french beans into small pieces.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1407,1,0,151,'Heat 2 tbsp oil in a deep non-stick pan, add 1/2 cup onions and sauté on a medium flame for 1 minute.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1408,2,0,151,'Add all the potatoes, french beans, carrots and mix well.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1409,3,0,151,'Cover and cook on a slow flame for 10 to 12 minutes or till the vegetables are cooked, while stirring occasionally. Sprinkle a little water (approx. 2 tbsp) to avoid the vegetables from burning.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1410,4,0,151,'Add  salt, 1/2 tsp chili powder, turmeric powder and 1/2 cup plain flour, mix well and sauté on a medium flame for 1 to 2 minutes. take off heat and let cool.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1411,5,1,151,'Mash the mixture using a potato masher.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1412,6,1,151,'Divide the mixture and shape them into flat ovals.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1413,7,1,151,'Dip each cutlet in the prepared plain flour mixture and roll in the bread crumbs till they are evenly coated from all the sides.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1414,8,0,151,'Heat 2 tbsp oil in a pan and fry each cutlet till it turns golden brown in color from both the sides. Drain on absorbent paper.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1415,0,1,152,'Cut lamb into 1” pieces. Finely chop onions. Mince garlic. Chop green chili pepper. Dice tomato, and chop cilantro leaves.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1416,1,0,152,'Heat 1/4 cup oil in an large pan.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1417,2,1,152,'Add 2 bay leaves, 1 cardamom, 1 cinnamon stick, 10 peppercorns, and 4 cloves.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1418,3,1,152,'Add onions.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1419,4,0,152,'When leaves begin to sizzle and fragrance starts to release. After onions added, sauté until light golden, about 12-15 minutes. Stir frequently.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1420,0,1,153,'Dice the potatoes and cut the green beans to 1⁄2 inch length.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1421,1,0,153,'After potatoes have been diced cook in salted water.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1422,5,1,152,'Season lamb with salt.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1423,6,0,152,'Continue cooking, stirring mix for about 20 more minutes. Add little water if necessary to keep food from sticking.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1424,2,1,153,'When potatoes are half done, add beans, vinegar, pepper, and garlic. (Can spear the garlic with a toothpick to find easier)');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1425,7,1,152,'Add 1/2 tsp turmeric, 2 tsp coriander powder, 1 tsp cayenne, and 1 1/2 tsp garam masala. Stir for 5 minutes. Add 2 Tbsp. water.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1426,3,0,153,'Simmer until the potatoes are done.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1427,8,1,152,'Add 4 cups of water.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1428,4,0,153,'Thicken with paste of flour and water.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1429,5,0,153,'Remove from heat.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1430,9,0,152,'Simmer for 15 minutes or until meat is getting tender.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1431,6,1,153,'Slowly stir in the sour cream (Do not cook just stir it in).');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1432,0,1,149,'Wash 3 tomatoes and 1 stalk of green onion. Measure out salt, sugar and sauce. (personal preference)');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1433,0,0,146,'Cut chicken into thin strips, mince the 3 garlic cloves and chop the 1 cup parsley');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1434,1,1,146,'Add 1.5lb of chicken breast, 1 cup parsley, the minced garlic, 1/3 cup olive oil, 1/4 cup red wine vinegar, 2 tbsp lemon juice, 1/2 tsp salt, 1/2 tsp pepper, 1/2 tsp oregano, and 1/2 tsp cayenne pepper in the ziplock bag.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1435,2,1,146,'Mix everything together in the bag.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1436,0,1,155,'Melt 3 Tbsp. butter. Finely grind almonds and pistachio nuts (if bought whole)');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1437,1,0,155,'Bring 2 cups fruit juice and 2 Tbsp. rose water to a boil.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1438,3,0,146,'Place the bag into the fridge and let sit for at least four hours.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1439,2,1,155,'Add 1 cup couscous. Stir well. Cover.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1440,3,0,155,'Remove from heat. Let stand for 15 minutes.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1441,4,1,155,'Fluff with a fork.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1442,4,0,146,'Remove the chicken from the marinade.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1443,5,1,155,'Rub melted butter into grains well.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1444,6,1,155,'Combine couscous with almonds and pistachio nuts.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1445,5,0,146,'Discard the remaining marinade.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1446,6,1,146,'Put chicken strips onto skewers.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1447,1,0,149,'Cut the stems off of the tomatoes. Peel skin off (optional). Cut tomatoes into wedges no thicker than 2.5 cm.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1448,7,0,146,'Grill skewers until thoroughly cooked.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1449,2,1,149,'Crack 3 eggs in a mixing bowl.
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1450,3,1,149,'Chop green onion.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1451,0,0,161,'Peel, core, and dice apple.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1452,0,1,159,'Measure out all the ingredients');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1453,1,0,161,'Help the little chef cut the 3 tomatoes, 1 onion, and 1 carrot.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1454,1,1,159,'Pour water in a kettle to boil (depending on little chef).');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1455,2,1,161,'Peel and dice tomatoes, dice onion, and slice the carrot.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1456,2,0,159,'Boil the water');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1457,3,1,161,'Pour water and lentils in the large pot, and mix in the vegetable bouillon.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1458,4,1,161,'Pour water and lentils in the large pot, and mix in the vegetable bouillon.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1459,3,1,159,'Help grind the spices in a mortar and pestle.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1460,5,0,161,'Bring the large pot to a boil, reduce heat to low, and simmer 20 minutes.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1461,4,1,159,'Place all of the dry ingredients into your favorite mug.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1462,6,1,161,'Stir the tomatoes, onion, carrot, apple, and peas into the large pot.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1463,5,0,159,'Once the water has come to a boil, pour a small amount (approximately 3-4 TBSP) into the mug and stir the dry mixture with the water until a thick paste is formed.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1464,6,0,159,'Add the rest of the water while stirring constantly, to ensure a lump-free consistency.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1465,7,0,161,'Stir in 1 garlic, 1 tbsp olive oil, 1/4 cups barbecue sauce, and 1/4 tsp paprika into the pot.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1466,8,0,161,'Continue to let simmer for 20 minutes.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1467,9,1,161,'Season with salt and pepper to serve.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1468,4,0,149,'Fry some of the green onions with oil, till you can smell the fragrance.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1469,0,1,160,'Dissolve yeast in a warm milk mixture; allow rising until it is double its size.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1470,1,1,160,'Combine the dry ingredients with butter until it is like a pie crust.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1471,2,1,160,'Add eggs, sour cream, and yeast mixture to the dry mixture and knead until smooth. Will take about 10 minutes and add flour if it gets sticky.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1472,3,1,160,'Mix filling ingredients together.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1473,4,1,160,'Once the dough has been divided, brush them with melted butter and spread a walnut mixture to cover.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1474,5,0,160,'Roll the dough up, pinch the ends and place in a greased 9 x 13 inch cake pan, put aside so it can double in size.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1475,6,0,160,'Bake at 325 degrees F for 35 to 40 minutes.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1476,0,0,163,'In a large skillet, heat 3 tablespoons of olive oil over medium-high heat.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1477,1,1,163,'Add onions and sauté until translucent. Add the garlic, sauté for another 2-3 minutes. Add 1/2 tsp turmeric powder, stir well.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1478,2,0,163,'Add the meat to the skillet, break into very small pieces and brown.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1479,3,1,163,'Then add 14 oz tomato sauce, 6 oz tomato paste, a cup of water, salt and pepper to taste.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1480,4,0,163,'Cover and simmer over low heat for about 45 minutes. Add chopped parsley near the end of cooking time.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1481,5,1,149,'Add in tomatoes.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1482,5,0,163,'Bring water to a boil and cook macaroni according to package directions.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1483,6,0,149,'Fry for a minute or two, or until tomatoes start to soften.
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1484,6,0,163,'Heat 2 tablespoons of oil in a heavy bottom nonstick pot');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1485,7,1,163,'Add some of the pasta, add a large scoop of the sauce, and add another layer of pasta');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1486,8,0,163,'Help the little chef build the pasta and sauce into the shape of a pyramid. Cover and cook for about 40 minutes over low heat.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1487,7,0,149,'Heat the wok/pan till it is very hot.
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1488,8,1,149,'Add in cooked tomatoes, rest of the chopped green onions and mix everything together.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1489,9,0,149,'Assist in putting eggs and tomatoes together.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1490,0,0,147,'Pour 3 cups milk into the pot and bring to a boil.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1491,1,1,147,'Add 1 cup rice and 1 tsp butter to the pot.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1492,2,1,147,'Continuously stir the mixture to prevent the rice from sticking to the bottom of the pot.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1493,3,0,147,'Lower the heat to low and let simmer for 20 minutes or so, until the rice has absorbed almost all of the liquid and is cooked through.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1494,0,1,164,'Dice onion and mince garlic.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1495,4,1,147,'Stir in the egg yolk, sugar, 1/2 tsp vanilla, and 1/2 tsp heavy cream and stir well to combine.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1496,1,1,164,'Season chicken legs with salt and pepper.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1497,5,1,147,'Stir in the 1/2 cup raisins and 1/2 tsp cinnamon.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1498,2,0,164,'Sauté onions and garlic in some oil until just golden.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1499,6,0,147,'Remove from heat and serve warm.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1500,3,0,164,'Add 1/2 tsp turmeric and stir.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1501,4,0,164,'Wait for chef koochooloo to season chicken, and add to the pot and sauté for a few minutes on each side.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1502,5,0,164,'Add 1/2 cup split peas along with 1 1/2 cups of water.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1503,6,0,164,'Add a bit of salt and pepper, cover and cook for 20 minutes.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1504,7,0,164,'Add 1 cup prunes and continue to cook covered for another 40 minutes.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1505,8,0,164,'Check for seasoning and add more salt if needed. Add 2 tbsp of brewed saffron and mix well. Cook for 5 minutes longer.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1506,0,1,158,'Wash, peel, and grate the cucumber.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1507,1,1,158,'Take 2 tbsp mint leaves off the stem.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1508,2,0,158,'Chop 1/4 cup walnuts.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1509,0,1,162,'Stir together all the ingredients to marinate the beef: white vinegar, vegetable oil, sweet onion, cilantro, fresh garlic,thyme, oregano, ground cumin, 1 1/2 teaspoons of salt, and 1 1/2 teaspoons of pepper.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1510,3,1,158,'Rinse the mint and pat dry.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1511,1,0,162,'Marinate the beef with the marinade the little chef just created, cover and chill for 4 hours.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1512,4,0,158,'Finely chop up the mint leaves after they are washed.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1513,5,1,158,'Combine 3 cups yogurt, grated cucumber, chopped walnuts, 1/2 cup raisins, chopped mint, 1 tsp salt and 1/4 tsp pepper and mix thoroughly with a big spoon.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1514,2,1,162,'Meanwhile, stir together all chimichurri ingredients: olive oil, red-wine vinegar, chopped onion, red bell pepper, garlic, chopped flat-leaf parsley, oregano leaves, hot red-pepper flakes, 3/4 teaspoon salt and 1/2 teaspoon pepper and let stand at least 2 hours.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1515,3,0,162,'Prepare a grill for direct-heat cooking over medium-hot charcoal (medium-high heat for gas).');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1516,4,0,162,'Remove beef from marinade and rub off excess (discard marinade).');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1517,5,0,162,'Oil grill rack, then grill (covered if using a gas grill), turning occasionally, 4 to 5 minutes for medium-rare.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1518,0,1,167,'Place the salted eggplants in a large container filled with water; put a heavy bowl or a heavy lid on top of the eggplants to hold them down for ten minutes, this will get rid of the bitterness. Remove eggplants from container and pat dry completely before frying.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1519,1,1,167,'Clean the yellow split peas thoroughly, 3 or 4 washes.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1520,2,1,167,'Dice the onion and mince the garlic.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1521,3,0,167,'Fry the eggplants in 1/2 cup of hot oil until brown on both sides. Remove from oil and place on a thick paper towel to take out the excess oil.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1522,4,0,167,'In a large saucepan, heat the oil, add chopped onions, saute until translucent then add the garlic, stir well. Put in turmeric, meat, salt and pepper and cinnamon. Mix thoroughly. Cook until meat is brown on all sides.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1523,5,0,167,'Add dry split peas, chopped tomatoes, tomato sauce and three cups of water to cover all the ingredients. Bring to a boil, lower the heat, cover and cook for an hour on medium heat.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1524,6,0,167,'Add the fried eggplant to the mixture, adjust the seasoning and add more water if needed.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1525,7,0,167,'Add the lime juice or two tablespoons of sour grapes (ghooreh).');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1526,8,0,167,'Cook for another 30 minutes or until meat is tender.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(1527,0,0,131,'Score the 2 cucumbers lengthwise on all sides with a fork. Cut the cucumber into thin slices.');
