INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(2140,6,0,249,'Meanwhile, in a skillet, melt the 1/2 tbsp butter and add the 1 chopped onion, 2 carrots, 2 celery and 2 garlic. Season with salt and pepper, and cook on medium heat for about 5 minutes, until vegetables are tender.');
