INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(2256,4,0,267,'Peel, quarter & core pears 20-30 min b4 serving. Cut crossways into slices Mix w/the lemon juice to stop them turning brown (I usually use my pastry brush for this)');
