INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(2603,3,0,324,'Add 1 tablespoon vegetable oil to the fat in the skillet. Place a tortilla in the skillet and cook until light golden but not crisp, about 30 seconds. Flip and cook for 30 seconds more. Transfer to an individual plate. Cook the remaining tortillas, adding a bit more oil to the pan if necessary.');
