INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(4365,6,0,553,'Season with salt and pepper and cook for another 5 - 10 minutes, stirring and adding water if necessary to prevent sticking.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(4366,0,1,555,'In a large bowl, toss potatoes, celery, pickle, onion and salt and pepper.');
