INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(4369,3,1,555,'Chill, covered, until serving time.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(4370,0,0,556,'Melt butter and add mustard, onion and poppy seeds. Cook slowly until onion is tender.');
