INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(4376,2,0,557,'Stir in the sugar and cook to caramelize the cabbage lightly.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(4377,3,0,557,'Add the vinegar and lemon juice and stir to scrape up the browned bits from the bottom of the pan.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(4378,4,1,557,'Season with salt and pepper and serve immediately.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(4379,0,1,558,'Combine ingredients in crock pot with cabbage on top.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(4380,1,0,558,'Cut meat to fit, if necessary.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(4381,2,0,558,'Cover and cook on low 10-12 hours; high 6-7 hours or auto 6-8 hours.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(4382,0,0,560,'Trim the meat, leaving a little of the fat and slice.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(4383,1,1,560,'Season the meat and veggies with salt, pepper, 2 teaspoons of parsley and the thyme.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(4384,2,1,560,'Layer the potatoes, meat and onions in a large casserole, starting and finishing with a layer of potatoes.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(4385,3,1,560,'Add the water and cover tightly.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(4386,4,0,560,'Cook in a preheated oven at 275 degrees for 2 1/2 hours, shake occasionally to prevent sticking, and check liquid now and then.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(4387,5,0,560,'Do not add unless absolutely necessary.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(4388,6,0,560,'The potatoes will thicken the finished stew so it should not be too runny.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(4389,7,0,560,'Just before serving, remove lid and brown the top under a hot broiler and sprinkle with remaining parsley.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(4390,8,0,560,'The potatoes on top should be nice and brown and crisp.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(4391,0,0,561,'Separate the eggs and beat the yolks.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(4392,1,1,561,'Add, the mashed potato to the yolks, mixing thoroughly.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(4393,2,1,561,'Add the lemon juice, chives, and salt and pepper.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(4394,3,1,561,'Whisk the egg whites until stiff.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(4395,4,0,561,'Fold the egg whites into the potato mixture.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(4396,5,0,561,'Melt the butter in a nonstick skillet or omelet pan over low-medium heat.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(4397,6,0,561,'Cook the omelet until golden (3-5 minutes), and then run under the broiler to finish and puff the omelet.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(4398,7,1,561,'Serve immediately.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(4399,0,1,559,'Cut the beef into strips about 4cm thick. Pack the meat into a smallish bowl, so that it fits tightly.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(4400,1,1,559,'Add the vinegar and Worcestershire sauce. Leave for 30 minutes.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(4401,2,1,559,'Meanwhile, mix the coriander and pepper together in another bowl');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(4402,3,0,559,'In a third bowl, mix the salt, sugar and bicarbonate of soda together.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(4403,4,1,559,'Remove the meat from the marinade, but reserve the marinade for later use.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(4404,5,1,559,'Add the beef to the bowl of spices, mixing it around until evenly coated. Save any spices that do not stick.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(4405,6,0,559,'Season with salt and pepper and cook for another 5 - 10 minutes, stirring and adding water if necessary to prevent sticking.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(4406,7,0,559,'Bury the spiced beef in the salt and the sugar mixture and leave it for 3 hours.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(4407,8,1,559,'Remove the beef from the brine and dip it back into the reserved vinegar marinade for another 5 minutes.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(4408,9,0,559,'Remove the beef from the marinade and using the vinegar, wash all the salt off (do not skip this bit or the biltong will be unbearably salty).');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(4409,10,1,559,'Squeeze the beef to remove as much liquid as possible. Roll in the spices once more. The meat is now ready for hanging.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(4410,11,0,559,'Cut lengths of string, and tie them tightly around one end of each beef strip. Tie the string to the top of your hanging contraption and make sure that they hang freely without touching anything on the sides or bottom.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(4411,12,1,559,'Leave the strips hanging for 3-20 days in a warm, dry place. It is difficult to say exactly how long it will take for the biltong to reach the correct stage, it is basically when the meat is as tough as old boots!');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(4412,0,1,562,'Pour the cinnamon and oats into boiling water until and stir until it begins to thicken.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(4413,1,0,562,'Lower the heat until it begins to simmer and cook for 30 minutes, stirring occasionally.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(4414,2,0,562,'It\'s important not to overcook the oats so that you get a nice nutty flavor.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(4415,3,1,562,'Add the remaining ingredients and enjoy!');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(4416,0,0,563,'Fry potatoes in 3 Tbsp. olive oil until browned and tender.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(4417,1,0,563,'Remove and keep warm.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(4418,2,0,563,'Add remaining oil to skillet, add shrimp.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(4419,3,0,563,'Cook for 2 minutes; add remaining ingredients and saute for 3 minutes.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(4420,4,1,563,'Return potatoes and mix well.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(4421,0,0,565,'In a large, lightly oiled pot steam fry the onion until it begins to soften [that means cook it in a bit of water].');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(4422,1,1,565,'Add flour and stir around completely.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(4423,2,1,565,'Add the remaining ingredients, mix well, bring to boil.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(4424,3,0,565,'Cover and simmer on low for 30 minutes or until vegetables are done.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(4425,4,1,565,'Taste and season.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(4426,5,0,565,'Note: The turnips should be large dice. The carrots round sliced.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(4427,0,1,566,'Place 1 cup water in 10-inch skillet over medium heat.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(4428,1,0,566,'Bring to a full boil; add carrots and asparagus.');
