INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(4430,3,0,566,'Drain, set aside, and keep warm.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(4431,4,1,566,'Melt butter in same skillet.');
