INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(4788,6,0,114,'If you have more time, you can add phyllo dough to the recipe and bake it to make if more authentic.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(4789,0,0,614,'Heat oil in skillet and fry potatoes until light brown and soft.');
