INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(4802,4,0,627,'Cook for a further 10-15 minutes and turn out on to a plate.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(4803,5,1,627,'Warm honey in the microwave until just warm enough to pour. Cut the meal into 8 slices, but do not separate the slices.');
INSERT INTO `recipe_step` (id,order,type,recipe_id,label) VALUES(4804,7,1,7,'To prepare the dipping sauce, combine the whole grain mustard, hummus, plain yogurt, Thai bail, and chives in a bowl. Season with salt to taste.');
